transforms.result:
.artist-detail {
  width: 100%;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/background/Background2.webp");
  background-repeat: repeat;
  background-position: top center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
}

.back-button-artists {
  position: absolute;
  top: 100px;
  left: 20px;
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  color: #fff;
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  text-decoration: none;
  transition: all 0.3s ease;
  z-index: 100;
}

.back-button-artists:hover {
  background-color: #2c2f11;
  color: #fff;
  transform: translateY(-2px);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.back-button-artists svg {
  font-size: 1.2rem;
}

@media (max-width: 768px) {
  .back-button-artists {
    padding: 8px 16px;
    font-size: 0.9rem;
  }
}

.artists-detail-hero {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
}

.hero-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.hero-title {
  position: absolute;
  bottom: 40px;
  left: 40px;
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 3rem;
  color: #fff;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
}

.artists-detail-product {
  margin: 0px auto;
  padding: 0 20px;
}

.section-title {
  font-family: "Meholrax", serif;
  font-size: 2rem;
  color: #2c2f11;
  margin-top: 30px;
  margin-bottom: 0px;
}

.artists-detail-item {
  text-decoration: none;
  display: block;
}

.product-image {
  width: 100%;
  aspect-ratio: 1;
  overflow: hidden;
}

.product-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.product-title {
  font-family: "Roboto", sans-serif;
  font-size: 1.5rem;
  color: #2c2f11;
  margin-bottom: 3px;
  text-align: center;
}

.product-price {
  font-family: "Roboto", sans-serif;
  font-size: 1.3rem;
  color: #666;
  text-align: center;
  margin-bottom: 40px;
}

/* Swiper customization */
.swiper-button-next,
.swiper-button-prev {
  color: #2c2f11;
}

.swiper-pagination {
  position: relative;
  margin-top: 20px;
}

.swiper-pagination-bullet {
  width: 40px !important;
  height: 4px !important;
  border-radius: 0 !important;
  margin: 0 5px !important;
  background: rgba(44, 47, 17, 0.2) !important;
  opacity: 1;
}

.swiper-pagination-bullet-active {
  background: #2c2f11 !important;
}

@media (max-width: 768px) {
  .artists-detail-hero {
    height: 300px;
  }

  .hero-title {
    font-size: 2.5rem;
    bottom: 20px;
    left: 20px;
  }

  .section-title {
    font-size: 1.8rem;
  }

  .product-title {
    font-size: 1rem;
  }

  .product-price {
    font-size: 0.9rem;
  }

  .swiper-pagination-bullet {
    width: 30px !important;
    height: 3px !important;
  }
}

.artists-detail-info {
  background-color: #2c2f11;
  color: white;
  padding: 60px 20px;
  margin: 40px 0;
}

.artists-detail-info-container {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 40px;
  align-items: start;
}

.artist-image {
  position: relative;
  width: 100%;
  padding-top: 120%; /* Tạo tỷ lệ khung hình 5:6 */
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.artist-image:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.4);
}

.artist-image img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center top;
  transition: transform 0.3s ease;
}

.artist-image:hover img {
  transform: scale(1.05);
}

.artist-image::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 30%;
  background: linear-gradient(to top, rgba(44, 47, 17, 0.8), transparent);
  pointer-events: none;
}

.artist-info {
  padding: 30px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  backdrop-filter: blur(10px);
}

.artist-info h2 {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 3rem;
  margin-bottom: 30px;
  color: yellowgreen;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.artist-info p {
  font-family: "Roboto", sans-serif;
  line-height: 1.8;
  margin-bottom: 25px;
  color: rgba(255, 255, 255, 0.9);
  font-size: 1.1rem;
}

.artist-info p strong {
  color: yellowgreen;
  font-weight: 600;
  font-size: 1.2rem;
  display: block;
  margin-bottom: 10px;
}

.artist-stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 25px;
  margin-top: 40px;
}

.stat-item {
  text-align: center;
  padding: 25px;
  background-color: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  transition: transform 0.3s ease, background-color 0.3s ease;
}

.stat-item:hover {
  transform: translateY(-5px);
  background-color: rgba(255, 255, 255, 0.15);
}

.stat-item h3 {
  font-size: 2.5rem;
  color: yellowgreen;
  margin-bottom: 15px;
  font-weight: 700;
}

.stat-item p {
  font-size: 1rem;
  margin: 0;
  color: rgba(255, 255, 255, 0.8);
  text-transform: uppercase;
  letter-spacing: 1px;
}

@media (max-width: 1024px) {
  .artists-detail-info-container {
    grid-template-columns: 1fr;
    gap: 30px;
  }

  .artist-image {
    padding-top: 80%;
    max-width: 600px;
    margin: 0 auto;
  }

  .artist-info h2 {
    font-size: 2.5rem;
    text-align: center;
  }

  .artist-stats {
    grid-template-columns: repeat(3, 1fr);
    gap: 20px;
  }
}

@media (max-width: 768px) {
  .artists-detail-info {
    padding: 40px 20px;
  }

  .artist-info {
    padding: 20px;
  }

  .artist-info h2 {
    font-size: 2rem;
  }

  .artist-info p {
    font-size: 1rem;
  }

  .stat-item {
    padding: 20px;
  }

  .stat-item h3 {
    font-size: 2rem;
  }

  .stat-item p {
    font-size: 0.9rem;
  }
}

@media (max-width: 480px) {
  .artist-stats {
    grid-template-columns: 1fr;
    gap: 15px;
  }

  .stat-item {
    padding: 15px;
  }
}

/* Artists All Products Section */
.artists-all-products {
  padding: 40px 20px;
}

.artists-all-products-title {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  text-align: center;
  color: #2c2f11;
  margin-bottom: 40px;
  font-size: 2.5rem;
}

.artists-products-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

.artist-product-card {
  position: relative;
  overflow: hidden;
  transition: transform 0.3s ease;
}

.artist-product-card:hover {
  transform: translateY(-5px);
}

.artist-product-card img {
  width: 100%;
  height: 300px;
  object-fit: cover;
}

.artist-product-content {
  padding: 15px;
  text-align: center;
}

.artist-product-content h3 {
  font-family: "Roboto", sans-serif;
  font-size: 1.1rem;
  margin-bottom: 8px;
  color: #2c2f11;
}

.artist-product-content p {
  font-size: 0.9rem;
  color: #000;
}

@media (min-width: 1024px) {
  .artists-products-grid {
    grid-template-columns: repeat(4, 1fr);
  }
}

/* Sort Section */
.sort-product-artists {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 30px;
  font-family: "Roboto", sans-serif;
  padding: 0px 20px;
}

.total-products-artists {
  font-size: 0.9rem;
  color: #666;
  display: flex;
  align-items: center;
  gap: 4px;
  white-space: nowrap;
}

.total-products-artists .total-count-artists {
  font-weight: 500;
  color: #2c2f11;
}

.sort-dropdown-container-artists {
  position: relative;
  display: flex;
  align-items: center;
}

.sort-dropdown-header-artists {
  display: flex;
  align-items: center;
  cursor: pointer;
  padding: 0px 12px;
  font-size: 0.85rem;
  color: #666;
  user-select: none;
  white-space: nowrap;
}

.sort-dropdown-header-artists.active {
  color: #2c2f11;
}

.sort-dropdown-header-artists svg {
  font-size: 24px;
  color: #2c2f11;
  transition: transform 0.3s ease;
}

.sort-dropdown-header-artists svg.rotated {
  transform: rotate(180deg);
}

.sort-dropdown-menu-artists {
  position: absolute;
  top: calc(100% + 4px);
  right: 0;
  background-color: white;
  border-radius: 4px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  min-width: 200px;
  z-index: 100;
  overflow: hidden;
}

.sort-option-artists {
  padding: 10px 16px;
  cursor: pointer;
  font-size: 0.9rem;
  color: #666;
  transition: all 0.2s ease;
}

.sort-option-artists:hover {
  background-color: #f5f5f5;
  color: #2c2f11;
}

.sort-option-artists.active {
  background-color: #f5f5f5;
  color: #2c2f11;
  font-weight: 500;
}

/* Filter Modal */
.search-filters-modal-artists {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #2c2f11;
  z-index: 1001;
  overflow-y: auto;
  color: white;
  animation: slideUp 0.3s ease-out;
}

.search-filters-header-artists {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 40px;
  position: relative;
}

.close-filters-button-artists {
  position: absolute;
  top: 0;
  right: 0;
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  color: yellowgreen;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: transform 0.2s ease;
}

.close-filters-button-artists:hover {
  transform: scale(1.1);
}

.close-filters-button-artists svg {
  width: 24px;
  height: 24px;
}

.search-filters-title-artists {
  font-family: "Meholrax", sans-serif;
  font-size: 2.5rem;
  text-align: center;
  color: yellowgreen;
  width: 100%;
  margin: 0;
}

.search-filters-content-artists {
  padding: 40px 20px;
  max-width: 800px;
  margin: 0 auto;
  padding-bottom: 100px;
  position: relative;
}

/* Pagination */
.result-pager-artists {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
  margin: 40px 0;
}

.result-pager-artists button {
  width: 40px;
  height: 40px;
  border: 1px solid #ddd;
  background: #2c2f11;
  border-radius: 4px;
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;
}

.result-pager-artists button.active {
  background-color: #2c2f11;
  color: yellowgreen;
  border-color: #fff;
}

.result-pager-artists button:hover:not(:disabled) {
  background-color: #2c2f11;
  color: white;
}

.result-pager-artists button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Filter Button */
.filter-button-artists {
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background-color: #2c2f11;
  color: white;
  border: none;
  padding: 12px 30px;
  border-radius: 30px;
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  cursor: pointer;
  z-index: 1000;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.filter-button-artists:hover {
  transform: translateX(-50%) translateY(-2px);
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
}

.apply-filters-button-artists {
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background-color: yellowgreen;
  color: #2c2f11;
  border: none;
  padding: 15px 40px;
  border-radius: 30px;
  font-family: "Roboto", sans-serif;
  font-size: 1.1rem;
  font-weight: 500;
  cursor: pointer;
  z-index: 1002;
  transition: all 0.2s ease;
  min-width: 200px;
  text-align: center;
}

.apply-filters-button-artists:hover:not(:disabled) {
  transform: translateX(-50%) translateY(-2px);
  box-shadow: 0 4px 12px rgba(154, 205, 50, 0.3);
}

.apply-filters-button-artists:disabled {
  background-color: #cccccc;
  color: #666666;
  cursor: not-allowed;
  opacity: 0.7;
}

@media (max-width: 768px) {
  .sort-product-artists {
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
  }

  .search-filters-title-artists {
    font-size: 2rem;
  }

  .result-pager-artists button {
    width: 35px;
    height: 35px;
  }
}
