.ticket-selection-containers {
  margin-top: 2rem;
}

.ticket-selection-containers h2 {
  margin-bottom: 1.5rem;
  font-size: 1.5rem;
  color: #2c2f11;
}

.package-infos {
  background: rgba(44, 47, 17, 0.05);
  border-radius: 8px;
  padding: 1.5rem;
  margin-bottom: 2rem;
}

.package-detailss h3 {
  color: #2c2f11;
  font-size: 1.2rem;
  margin-bottom: 1rem;
}

.package-detailss ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.package-detailss li {
  position: relative;
  padding-left: 1.5rem;
  margin-bottom: 0.5rem;
  color: #2c2f11;
}

.package-detailss li::before {
  content: "•";
  position: absolute;
  left: 0;
  color: #2c2f11;
}

.package-notes,
.package-times {
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid rgba(44, 47, 17, 0.1);
  color: #2c2f11;
}

.tickets-lists {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.ticket-items {
  border: 1px solid #2c2f11;
  border-radius: 8px;
  padding: 1.5rem;

}

.ticket-headers {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
}

.ticket-headers h3 {
  font-size: 1.1rem;
  color: #2c2f11;
}

.prices {
  font-weight: bold;
  color: #2c2f11;
}

.quantity-controls {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
}

.quantity-controls button {
  width: 36px;
  height: 36px;
  border: 1px solid #2c2f11;
  background: white;
  color: #2c2f11;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1.2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
}

.quantity-controls button:hover:not(:disabled) {
  background: white;
  color: #2c2f11;
}

.quantity-controls button:disabled {
  background: #f0f0f0;
  border-color: #ddd;
  color: #999;
  cursor: not-allowed;
}

.quantity-controls input {
  width: 60px;
  height: 36px;
  border: 1px solid #2c2f11;
  border-radius: 4px;
  text-align: center;
  background-color: #fff;
  font-size: 1rem;
  color: #2c2f11;
}

.visitor-namess {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.visitor-inputs {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.visitor-inputs label {
  font-size: 0.9rem;
  color: #2c2f11;
}

.visitor-inputs input {
  padding: 0.8rem;
  border: 1px solid #2c2f11;
  border-radius: 4px;
  font-size: 0.9rem;
  background-color: #fff;
  color: #2c2f11;
  transition: all 0.3s ease;
}

.visitor-inputs input:focus {
  outline: none;
  border-color: #2c2f11;
  box-shadow: 0 0 0 2px rgba(44, 47, 17, 0.2);
}

@media (max-width: 768px) {
  .package-infos {
    padding: 1rem;
  }

  .ticket-items {
    padding: 1rem;
  }

  .quantity-controls button {
    width: 32px;
    height: 32px;
  }
}

