.artstay-container {
  position: relative;
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  z-index: 1;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  width: 100%;
  backdrop-filter: blur(4px);
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
  line-height: 1.6;
  color: #333;
}

.artstay-container::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    135deg,
    rgba(44, 47, 17, 0.05) 0%,
    rgba(149, 161, 37, 0.05) 100%
  );
  pointer-events: none;
  z-index: -1;
}

.artstay-title {
  font-size: 2.5rem;
  padding: 2rem 0;
  font-weight: 700;
  color: #1a1a1a;
  letter-spacing: 2px;
  text-align: center;
  line-height: 1.3;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
  position: relative;
  margin-bottom: 2rem;
}

.artstay-title::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 150px;
  height: 3px;
  background: linear-gradient(90deg, transparent, #95a125, transparent);
}

.artstay-container h2 {
  font-size: 1.8rem;
  font-family: "Meholrax", sans-serif;
  align-items: center;
  text-align: center;
  color: #2c3e50;
  margin: 30px 0 20px 0;
  font-weight: 600;
  letter-spacing: 0.5px;
  border-bottom: 2px solid #eee;
  padding-bottom: 0.5rem;
}

.artstay-container p,
.artstay-container ul,
.artstay-container li {
  font-size: 1.15rem;
  font-family: "Roboto", sans-serif;
  color: #333;
  text-align: justify;
  line-height: 1.8;
  margin-bottom: 16px;
}

.artstay-container ul {
  margin: 15px 0;
  list-style-type: none;
  padding-left: 0;
}

.artstay-container li {
  margin-bottom: 12px;
  position: relative;
  padding-left: 1.5rem;
}

.artstay-container li:before {
  content: "•";
  position: absolute;
  left: 0;
  color: #666;
}

.artstay-container a {
  color: #2c2f11;
  font-family: "Roboto", sans-serif;
  font-weight: 500;
  text-decoration: none;
  border-bottom: 1px solid transparent;
  transition: all 0.3s ease;
  display: inline-flex;
  align-items: center;
  gap: 6px;
}

.artstay-container a:hover {
  color: #95a125;
  border-bottom-color: #95a125;
}

.artstay-container img {
  width: 100%;
  height: auto;
  border-radius: 8px;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.form-group {
  margin-bottom: 24px;
}

.form-label {
  display: block;
  font-weight: 500;
  font-family: "Roboto", sans-serif !important;
  margin-bottom: 8px;
  color: #2c2f11;
}

.form-input,
.artstay-container textarea {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e2e8f0;
  font-family: "Roboto", sans-serif !important;
  border-radius: 12px;
  font-size: 1.1rem;
  background: rgba(255, 255, 255, 0.9);
  transition: all 0.3s ease;
  font-family: inherit;
}

.form-input:focus,
.artstay-container textarea:focus {
  border-color: #1976d2;
  outline: none;
  box-shadow: 0 0 0 3px rgba(25, 118, 210, 0.1);
}

.btn-submit {
  width: 100%;
  background: linear-gradient(135deg, #2c2f11 0%, #95a125 100%);
  color: #fff;
  padding: 1.2rem;
  border: none;
  border-radius: 12px;
  font-weight: 600;
  font-size: 1.2rem;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-bottom: 15px;
  position: relative;
  overflow: hidden;
}

.btn-submit::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.2),
    transparent
  );
  transition: 0.5s;
}

.btn-submit:hover::before {
  left: 100%;
}

.btn-submit:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(149, 161, 37, 0.3);
  color: #fff;
}

.btn-submit:disabled {
  opacity: 0.7;
  cursor: not-allowed;
  transform: none;
}

.form-error {
  color: #dc2626;
  font-size: 0.95rem;
  margin-top: 6px;
  display: flex;
  align-items: center;
  gap: 4px;
}

.success-message {
  border-radius: 12px;
  margin-bottom: 24px;
  text-align: center;
  width: 100%;
  font-size: 1.1rem;
}

@media (max-width: 768px) {
  .artstay-container {
    padding: 1rem;
  }

  .artstay-title {
    font-size: 2rem;
  }

  .artstay-container h2 {
    font-size: 1.5rem;
  }

  .artstay-container p,
  .artstay-container ul,
  .artstay-container li {
    font-size: 1.05rem;
  }

  .artstay-container iframe {
    width: 100%;
  }
}

.form-row {
  display: flex;
  gap: 20px;
  margin-bottom: 24px;
}

.form-row > div {
  flex: 1;
}

@media (max-width: 600px) {
  .form-row {
    flex-direction: column;
    gap: 16px;
  }
}

/* Success Modal Styles */
.booking-success-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.booking-success-modal.open {
  opacity: 1;
  visibility: visible;
}

.success-modal-content {
  background: linear-gradient(135deg, #fff 0%, #f8f9fa 100%);
  padding: 2.5rem;
  border-radius: 20px;
  max-width: 500px;
  width: 90%;
  text-align: center;
  position: relative;
  transform: translateY(20px);
  transition: transform 0.3s ease;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.booking-success-modal.open .success-modal-content {
  transform: translateY(0);
}

.success-icon {
  width: 80px;
  height: 80px;
  background: #2c2f11;
  border-radius: 50%;
  color: yellowgreen;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1.5rem;
  animation: scaleIn 0.5s ease;
}

.success-title {
  font-size: 1.8rem;
  color: #2c2f11;
  margin-bottom: 1rem;
  font-family: "Meholrax", sans-serif;
}

.success-message {
  color: #666;
  font-size: 1.1rem;
  line-height: 0.5;
  margin-bottom: 2rem;
}

.success-animation {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  overflow: hidden;
}

.confetti-piece {
  position: absolute;
  animation: fall 3s linear infinite;
}

.btn-close-success {
  background: linear-gradient(135deg, #2c2f11 0%, #95a125 100%);
  color: white;
  border: none;
  padding: 12px 30px;
  border-radius: 8px;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.btn-close-success:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(149, 161, 37, 0.3);
}

@keyframes scaleIn {
  from {
    transform: scale(0);
    opacity: 0;
  }
  to {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes fall {
  0% {
    transform: translateY(-100px) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(1000px) rotate(360deg);
    opacity: 0;
  }
}

/* Add iframe styles */
.artstay-container iframe {
  width: 100%;
  max-width: 100%;
  margin: 0 auto;
  display: block;
  border: none;
  background: rgba(255, 255, 255, 0.9);
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
}

.intro-section,
.location-section,
.about-sections,
.exhibition-section,
.conditions-section,
.program-time-section,
.services-section,
.sponsorship-section,
.closing-section {
  margin-bottom: 3rem;
}

.greeting {
  font-style: italic;
  font-size: 1.2rem;
  margin-bottom: 1.5rem;
  color: #666;
}

h2 {
  font-size: 1.8rem;
  color: #2c3e50;
  margin-bottom: 1.5rem;
  border-bottom: 2px solid #eee;
  padding-bottom: 0.5rem;
}

p {
  margin-bottom: 1.2rem;
  text-align: justify;
}

ul {
  list-style-type: none;
  padding-left: 0;
}

li {
  margin-bottom: 1rem;
  padding-left: 1.5rem;
  position: relative;
}

li:before {
  content: "•";
  position: absolute;
  left: 0;
  color: #666;
}

.closing-section {
  padding: 1rem;
  border-radius: 8px;
  margin: 2rem 0;
  font-family: "Roboto", sans-serif;
}

.closing-section p {
  color: #666;
  text-align: center;
}

em {
  font-style: italic;
}

iframe {
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin-top: 2rem;
}

.registration-buttons {
  display: flex;
  gap: 20px;
  justify-content: center;
  margin: 2rem 0;
}

.registration-link {
  display: inline-block;
  padding: 15px 30px;
  background: linear-gradient(135deg, #2c2f11 0%, #95a125 100%);
  color: white !important;
  text-decoration: none;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  transition: all 0.3s ease;
  border: none !important;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.registration-link:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
  color: white !important;
}

.registration-link svg {
  margin-right: 8px;
  vertical-align: middle;
}
