    // Listen for mobile menu class changes
    const mobileMenuOverlay = document.querySelector(".mobile-menu-overlay");
    if (mobileMenuOverlay) {
      const observer = new MutationObserver((mutations) => {
        mutations.forEach((mutation) => {
