          mutation.attributeName === "class")
          {
            if (mobileMenuOverlay.classList.contains("show")) {
              handleMobileMenuShow();
            } else {
              handleMobileMenuHide();
            }
          }
        });
      });

      observer.observe(mobileMenuOverlay, {
        attributes: true
      });

      return () => observer.disconnect();
    }
  }, []);

