.exhibitions-page-container {
  padding: 40px 20px;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/background/Background2.webp");
  background-repeat: repeat;
  background-position: top center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  min-height: 100vh;
}

.exhibitions-page-header {
  max-width: 1200px;
  margin: 0 auto 40px;
  text-align: center;
}

.exhibitions-page-title {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 3rem;
  margin-bottom: 20px;
  color: #2c2f11;
}

.exhibitions-page-count {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 20px;
  font-size: 1.2rem;
  flex-wrap: nowrap;
  white-space: nowrap;
  color: #000;
}

.exhibitions-page-toggle-container {
  display: flex;
  align-items: center;
  gap: 10px;
  white-space: nowrap;
}

/* Toggle Switch Styles */
.exhibitions-page-toggle {
  position: relative;
  display: inline-block;
  width: 60px;
  height: 34px;
  flex-shrink: 0;
}

.exhibitions-page-toggle input {
  opacity: 0;
  width: 0;
  height: 0;
}

.exhibitions-page-slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  transition: 0.4s;
  border-radius: 34px;
}

.exhibitions-page-slider:before {
  position: absolute;
  content: "";
  height: 26px;
  width: 26px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: 0.4s;
  border-radius: 50%;
}

input:checked + .exhibitions-page-slider {
  background-color: #2c2f11;
}

input:checked + .exhibitions-page-slider:before {
  transform: translateX(26px);
}

/* Grid Layout */
.exhibitions-page-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
}

/* Exhibition Card Styles */
.exhibitions-page-card {
  overflow: hidden;
  transition: transform 0.3s ease;
  text-decoration: none;
  color: inherit;
  width: 100%;
  height: 100%;
}

.exhibitions-page-card:hover {
  transform: translateY(-5px);
}

.exhibitions-page-card-image {
  position: relative;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
}

.exhibitions-page-card-image img {
  width: 80%;
  height: auto;
  object-fit: contain;
  transition: transform 0.3s ease;
}

.exhibitions-page-card:hover .exhibitions-page-card-image img {
  transform: scale(1.05);
}

.exhibitions-page-card-status {
  position: absolute;
  display: none;
  top: 10px;
  right: 10px;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 500;
  color: white;
}

.exhibitions-page-card-status.inprogress {
  background-color: yellowgreen;
}

.exhibitions-page-card-status.close {
  background-color: #666;
}

.exhibitions-page-card-info {
  padding: 10px;
  text-align: left;
}

.exhibitions-page-card-info h2 {
  font-size: 1.4rem;
  margin-bottom: 10px;
  color: #2c2f11;
  line-height: 1.3;
}

.exhibitions-page-card-date {
  color: #666;
  margin-bottom: 8px;
}

.exhibitions-page-card-products {
  color: #888;
  font-size: 0.9rem;
}

/* Responsive Design */
@media (max-width: 768px) {
  .exhibitions-page-container {
    padding: 20px 10px;
  }

  .exhibitions-page-title {
    font-size: 2.5rem;
  }

  .exhibitions-page-count {
    gap: 55px;
    font-size: 1.1rem;
  }

  .exhibitions-page-grid {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 20px;
  }
}

@media (max-width: 480px) {
  .exhibitions-page-title {
    font-size: 2rem;
  }

  .exhibitions-page-grid {
    grid-template-columns: 1fr;
  }
}
