/* Remove Google Fonts imports and use local fonts */
/* Hero Container Styles */
.hero-container-home {
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
}

.hero-background {
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  position: relative;
}

.hero-background::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.5),
    rgba(0, 0, 0, 0.7)
  );
}

.hero-content {
  position: absolute;
  margin: auto;
  bottom: 30%;
  right: 5%;
  text-align: center;
  z-index: 2;
  width: 100%;
}

.hero-titles {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 4rem;
  color: #ffffff;
  margin-bottom: 2rem;
  margin-left: 2rem;
  line-height: 1.2;
  text-transform: uppercase;
  letter-spacing: 2px;
}
.hero-titles-sub {
  font-family: "Meholrax", serif;
  font-size: 1.7rem;
  color: #ffffff;
}

.book-ticket-button {
  background: #2c2f11;
  color: yellowgreen;
  border: none;
  padding: 1rem 3rem;
  font-family: "Roboto", sans-serif;
  border: 1px solid #ffffff;
  font-size: 1.2rem;
  border-radius: 10px;
  width: 200px;
  margin-left: 2rem;
  cursor: pointer;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.book-ticket-button:hover {
  background: #3a3d1e;
  transform: translateY(-2px);
}

/* Responsive styles */
@media (max-width: 1200px) {
  .hero-titles {
    font-size: 3.5rem;
  }
}

@media (max-width: 768px) {
  .hero-titles {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
  }

  .book-ticket-button {
    padding: 0.8rem 2rem;
    font-size: 1rem;
  }
}

@media (max-width: 480px) {
  .hero-titles {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }

  .book-ticket-button {
    padding: 0.7rem 1.8rem;
    font-size: 0.9rem;
  }
}

/* Custom font loading */
@font-face {
  font-family: "Mythical-Prince";
  src: url("../../../assets/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: "LouvreSerif";
  src: url("../../assets/fonts/louvreserif/400/latin.woff2") format("woff2");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: "Roboto";
  src: url("../../assets/fonts/roboto/400/latin.woff2") format("woff2");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

/* Mobile controller for prepare your visit */
.mobile-hero-controller {
  display: none;
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  background-color: #000;
  color: white;
  z-index: 100;
  box-shadow: 0 -2px 8px rgba(0, 0, 0, 0.15);
}

.mobile-prepare-visit {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 18px 20px;
  font-family: "Montserrat", Arial, sans-serif;
  font-weight: 600;
  font-size: 14px;
  background-color: #2c2f11;
  cursor: pointer;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.prepare-visit-arrow {
  transition: transform 0.3s ease;
}

/* Info Panel Styles */
.hero-info-panel {
  position: absolute;
  width: 55%;
  bottom: -20px;
  right: 0;
  background-color: #2c2f11;
  color: white;
  padding: 0;
  z-index: 10;
  box-shadow: 0 -5px 20px rgba(0, 0, 0, 0.2);
  margin: 0;
}

.info-panel-content {
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1280px;
  margin: 0 auto;
  background-color: #2c2f11;
  padding: 20px;
}

.info-panel-left {
  flex: 1;
}

.info-panel-titles {
  font-family: "Montserrat", Arial, sans-serif;
  font-size: 18px;
  color: #fff;
  font-weight: 600;
  margin: 0 0 10px 0;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.info-panel-status {
  font-family: "Roboto", Arial, sans-serif;
  font-size: 14px;
  font-weight: 400;
  margin: 0 0 5px 0;
  color: rgba(255, 255, 255, 0.8);
}

.info-panel-hours {
  font-family: "Montserrat", Arial, sans-serif;
  font-size: 16px;
  font-weight: 600;
  margin: 0;
  display: flex;
  align-items: center;
}

.hours-arrow {
  margin: 0 8px;
  display: flex;
  align-items: center;
}

.info-panel-separator {
  width: 1px;
  height: 100px;
  background-color: rgba(255, 255, 255, 0.2);
  margin: 0 30px;
}

.info-panel-right {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.info-panel-button {
  padding: 12px 25px;
  border-radius: 30px;
  font-family: "Roboto", Arial, sans-serif;
  font-size: 14px;
  font-weight: 500;
  text-align: center;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  width: 200px;
}
.info-panel-button:hover {
  text-decoration: underline;
}

.btn-primary {
  background-color: #00695c;
  color: white;
  border: none;
}

.btn-primary:hover {
  background-color: #00897b;
}

.btn-secondary {
  background-color: #ffffff;
  color: #000000;
  border: 1px solid rgba(255, 255, 255, 0.7);
}

.button-icon {
  margin-right: 10px;
}

@media (max-width: 992px) {
  .hero-titles {
    font-size: 40px;
    line-height: 48px;
  }
}

@media (max-width: 768px) {
  .hero-titles {
    font-size: 30px;
    line-height: 38px;
  }

  /* Show mobile controller and adjust panel */
  .mobile-hero-controller {
    display: block;
  }

  .hero-info-panel {
    position: fixed;
    width: 100%;
    height: auto;
    bottom: 56px; /* Higher than mobile-hero-controller */
    right: 0;
    left: 0;
    background-color: #1a1a1a;
    transform: translateY(100%);
    transition: transform 0.3s ease;
    z-index: 99;
  }

  .hero-info-panel.open {
    transform: translateY(0);
  }

  .info-panel-content {
    flex-direction: column;
    align-items: flex-start;
    padding: 20px;
  }

  .info-panel-separator {
    width: 100%;
    height: 1px;
    margin: 20px 0;
  }

  .info-panel-right {
    margin-top: 20px;
    width: 100%;
  }

  .info-panel-button {
    width: 100%;
  }
}

@media (max-width: 576px) {
  .hero-titles {
    font-size: 3.2rem;
    line-height: 68px;
    letter-spacing: 1px;
  }

  .info-panel-titles {
    font-size: 16px;
  }

  .info-panel-status {
    font-size: 13px;
  }

  .info-panel-hours {
    font-size: 14px;
  }

  .info-panel-button {
    padding: 10px 20px;
    font-size: 13px;
  }
}

/* Extra small devices */
@media (max-width: 375px) {
  .hero-titles {
    font-size: 22px;
    line-height: 28px;
  }

  .info-panel-content {
    padding: 15px;
  }

  .info-panel-separator {
    margin: 15px 0;
  }

  .info-panel-button {
    padding: 8px 16px;
  }
}

/* End of Hero CSS */

