/* Enhanced Modal Styles - Updated to match sample design */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.95);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: modalFadeIn 0.3s ease;
  backdrop-filter: blur(8px);
}

.modal-content {
  position: relative;
  width: 100%;
  height: auto;
  max-height: 100vh;
  border-radius: 0;
  max-width: none;
  background: #000;
  overflow: auto;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.5);
  animation: modalSlideUp 0.4s cubic-bezier(0.19, 1, 0.22, 1) forwards;
  display: flex;
  flex-direction: column;
}

.modal-close {
  position: absolute;
  top: 20px;
  right: 20px;
  width: 44px;
  height: 44px;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 10;
  backdrop-filter: blur(10px);
}

.modal-close:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.1);
}

.modal-close svg {
  width: 20px;
  height: 20px;
}

/* Image section with black background */
.modal-image-section {
  position: relative;
  flex: 1;
  background: #000;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 60%;
}

/* Navigation arrows */
.modal-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 5;
  backdrop-filter: blur(10px);
}

.modal-nav:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateY(-50%) scale(1.1);
}

.modal-nav.modal-prev {
  left: 20px;
}

.modal-nav.modal-next {
  right: 20px;
}

.modal-nav svg {
  width: 24px;
  height: 24px;
}

.modal-image-container {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
}

.modal-image {
  max-width: 100%;
  max-height: 100%;
  object-fit: contain;
  border-radius: 8px;
}

/* Dots navigation */
.modal-dots-navigation {
  position: absolute;
  bottom: 80px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 8px;
  z-index: 5;
}

.modal-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  border: none;
  background: rgba(255, 255, 255, 0.4);
  cursor: pointer;
  transition: all 0.3s ease;
}

.modal-dot:hover {
  background: rgba(255, 255, 255, 0.7);
}

.modal-dot.active {
  background: #fff;
  transform: scale(1.2);
}

/* Download button */
.modal-download-btn {
  position: absolute;
  bottom: 20px;
  right: 20px;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 25px;
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 5;
  backdrop-filter: blur(10px);
  font-size: 14px;
  font-weight: 500;
}

.modal-download-btn:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.05);
}

.modal-download-btn svg {
  width: 18px;
  height: 18px;
}

/* Counter */
.modal-counter {
  position: absolute;
  top: 20px;
  left: 20px;
  background: rgba(255, 255, 255, 0.1);
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 500;
  backdrop-filter: blur(10px);
  z-index: 5;
}

/* Info section with white background */
.modal-info-section {
  background: #fff;
  padding: 24px;
  border-top-left-radius: 20px;
  border-top-right-radius: 20px;
  margin-top: -20px;
  position: relative;
  z-index: 2;
}

.modal-title-main {
  font-size: 24px;
  font-weight: 700;
  color: #333;
  margin-bottom: 20px;
  line-height: 1.2;
}

.modal-metadata-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 16px;
  margin-bottom: 20px;
}

.modal-meta-item {
  text-align: left;
}

.modal-meta-label {
  font-size: 12px;
  color: #888;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 4px;
}

.modal-meta-value {
  font-size: 14px;
  color: var(--primary-color);
  font-weight: 600;
  line-height: 1.3;
}

.modal-tags-section {
  margin-bottom: 16px;
}

.modal-tag-item {
  display: inline-block;
  background: #2c2f11;
  color: #fff;
  padding: 6px 12px;
  border-radius: 15px;
  font-size: 12px;
  font-weight: 500;
  margin-right: 8px;
  margin-bottom: 8px;
}

.modal-description-text {
  font-size: 14px;
  line-height: 1.5;
  color: #666;
  margin-bottom: 20px;
}

.modal-location-section {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  padding: 16px;
  background: #f8f9fa;
  border-radius: 12px;
  margin-bottom: 24px;
}

.modal-location-icon {
  width: 20px;
  height: 20px;
  color: #888;
  margin-top: 2px;
  flex-shrink: 0;
}

.modal-location-content {
  flex: 1;
}

.modal-location-title {
  font-size: 14px;
  font-weight: 600;
  color: #333;
  margin-bottom: 4px;
}

.modal-location-text {
  font-size: 13px;
  color: #666;
  line-height: 1.4;
}

.modal-visit-button {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  width: 100%;
  background: #2c2f11;
  color: #fff;
  border: none;
  border-radius: 12px;
  padding: 16px 24px;
  font-size: 16px;
  font-weight: 600;
  text-decoration: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.modal-visit-button:hover {
  background: #3a3d1e;
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(44, 47, 17, 0.3);
  color: #fff;
}

.modal-visit-button svg {
  width: 18px;
  height: 18px;
}

/* Mobile responsive */
@media (max-width: 576px) {
  .modal-content {
    width: 100%;
    height: 100vh;
    border-radius: 0;
    max-width: none;
  }

  .modal-close {
    top: 15px;
    right: 15px;
    width: 40px;
    height: 40px;
  }

  .modal-nav {
    width: 44px;
    height: 44px;
  }

  .modal-nav.modal-prev {
    left: 15px;
  }

  .modal-nav.modal-next {
    right: 15px;
  }

  .modal-download-btn {
    bottom: 15px;
    right: 15px;
    padding: 10px 14px;
    font-size: 13px;
  }

  .modal-counter {
    top: 15px;
    left: 15px;
    padding: 6px 12px;
    font-size: 13px;
  }

  .modal-dots-navigation {
    bottom: 70px;
    gap: 6px;
  }

  .modal-dot {
    width: 6px;
    height: 6px;
  }

  .modal-info-section {
    padding: 20px;
    border-radius: 16px 16px 0 0;
  }

  .modal-title-main {
    font-size: 20px;
    margin-bottom: 16px;
  }

  .modal-metadata-grid {
    grid-template-columns: repeat(3, 1fr);
    gap: 12px;
    margin-bottom: 16px;
  }

  .modal-meta-label {
    font-size: 11px;
  }

  .modal-meta-value {
    font-size: 13px;
  }

  .modal-location-section {
    padding: 14px;
    margin-bottom: 20px;
  }

  .modal-visit-button {
    padding: 14px 20px;
    font-size: 15px;
  }
}

@keyframes modalFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes modalSlideUp {
  from {
    opacity: 0;
    transform: translateY(30px) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}
