  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

.dupin-plus-page {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  color: #2c2f11;
  font-weight: 600;
  min-height: 100vh;
  font-family: "Roboto", sans-serif;
}

.dupin-plus-header {
  display: none;
}

/* Hero Slideshow Styles */
.dupinplus-hero {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: #2c2f11;
}

.hero-slideshow {
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.hero-slide {
  position: absolute;
  width: 100%;
  height: 100%;
  opacity: 0;
  transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  display: flex;
  align-items: center;
  justify-content: center;
}

.hero-slide.active {
  opacity: 1;
  z-index: 1;
}

.hero-image-dupin {
  height: 100%;
  width: auto;
  max-width: none;
  object-fit: contain;
}

.hero-content-dupin {
  position: absolute;
  bottom: 20%;
  left: 0;
  right: 0;
  color: #fff;
  z-index: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  padding: 0 2rem;
}

.hero-text {
  max-width: 800px;
  margin-bottom: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.hero-category {
  display: inline-block;
  background-color: #2c2f11;
  padding: 0.6rem 2rem;
  display: none;
  border-radius: 50px;
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 2px;
  margin-bottom: 1.5rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
}

.hero-title {
  font-size: 3rem;
  font-weight: 600;
  display: none;
  margin: 0;
  line-height: 1.3;
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  color: #fff;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  max-width: 80%;
}

.hero-play-button {
  display: flex;
  align-items: center;
  gap: 1rem;
  background-color: #2c2f11;
  color: #fff;
  border: none;
  padding: 1rem 2.5rem;
  border-radius: 50px;
  cursor: pointer;
  font-size: 1.1rem;
  font-weight: 500;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  border: 1px solid yellowgreen;
}

.hero-play-button:hover {
  background-color: #9c7436;
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(179, 135, 65, 0.4);
}

.hero-play-button svg {
  width: 24px;
  height: 24px;
  transition: transform 0.3s ease;
}

.hero-play-button:hover svg {
  transform: scale(1.1);
}

.hero-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: rgba(0, 0, 0, 0.5);
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: #fff;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 3;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  backdrop-filter: blur(10px);
}

.hero-nav:hover {
  background: rgba(179, 135, 65, 0.8);
  border-color: #b38741;
  transform: translateY(-50%) scale(1.1);
}

.hero-nav.prev {
  left: 2rem;
}

.hero-nav.next {
  right: 2rem;
}

.hero-dots-dupin {
  position: absolute;
  bottom: 4rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 1rem;
  z-index: 3;
  padding: 0.8rem 1.2rem;
  border-radius: 50px;
  background: #2c2f11;
  backdrop-filter: blur(10px);
}

.hero-dot-dupin {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background-color: rgba(255, 255, 255, 0.3);
  border: 2px solid rgba(255, 255, 255, 0.5);
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  padding: 0;
}

.hero-dot-dupin:hover {
  background-color: rgba(179, 135, 65, 0.5);
  border-color: #b38741;
}

.hero-dot-dupin.active {
  background-color: #b38741;
  border-color: #b38741;
  transform: scale(1.2);
}

