/* Videos Grid */
.videos-grid {
  padding: 4rem;
  max-width: 1400px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 4rem;
}

.videos-grid-contents {
  width: 100%;
}

.videos-grid-header {
  margin-bottom: 2rem;
}

.videos-grid-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  color: #2c2f11;
  margin: 0;
  margin-bottom: 0.5rem;
}

.videos-count {
  font-family: "Roboto", serif;
  font-size: 1.1rem;
  color: #666;
}

/* Grid layout for less than 4 videos */
.videos-grid-fixed {
  display: grid;
  gap: 2rem;
}

@media screen and (min-width: 769px) {
  .videos-grid-fixed {
    grid-template-columns: repeat(3, 1fr);
  }
}

@media screen and (max-width: 768px) {
  .videos-grid-fixed {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* Slide layout for 4 or more videos */
.videos-grid-slide {
  position: relative;
  width: 100%;
}

.videos-slide-container {
  display: flex;
  gap: 2rem;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  scrollbar-width: none;
  -ms-overflow-style: none;
  padding: 1rem 0;
}

.videos-slide-container::-webkit-scrollbar {
  display: none;
}

.videos-grid-slide .video-card {
  flex: 0 0 calc(33.333% - 1.33rem);
  scroll-snap-align: start;
}

@media screen and (max-width: 768px) {
  .videos-grid-slide .video-card {
    flex: 0 0 calc(50% - 1rem);
  }
}

.slide-nav-dupin {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(44, 47, 17, 0.9);
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: #b38741;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 2;
  transition: all 0.3s ease;
}

.slide-nav-dupin:hover {
  background: rgba(179, 135, 65, 0.2);
  transform: translateY(-50%) scale(1.1);
}

.slide-nav-dupin.prev {
  left: -20px;
}

.slide-nav-dupin.next {
  right: -20px;
}

.slide-nav-dupin svg {
  width: 24px;
  height: 24px;
}

/* Video Card Styles */
.video-card {
  border-radius: 12px;
  overflow: hidden;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  cursor: pointer;
  border: 1px solid rgba(179, 135, 65, 0.2);
}

.video-card:hover {
  transform: translateY(-10px);
  box-shadow: 0 15px 40px -5px rgba(0, 0, 0, 0.4);
  border-color: #b38741;
}

.video-thumbnail-container {
  position: relative;
  width: 100%;
  padding-top: 56.25%;
  overflow: hidden;
}

.video-thumbnail {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.video-card:hover .video-thumbnail {
  transform: scale(1.05);
}

.video-info {
  padding: 1.5rem;
}

.video-title {
  font-family: "Meholrax", serif;
  font-size: 1.2rem;
  margin: 0;
  color: #2c2f11;
  line-height: 1.4;
  margin-bottom: 0.5rem;
}

.video-subtitle {
  font-family: "Roboto", serif;
  font-size: 0.9rem;
  color: #666;
  display: block;
}

/* Responsive Adjustments */
@media screen and (max-width: 768px) {
  .videos-grid {
    padding: 1rem;
    gap: 2rem;
  }

  .videos-grid-contents {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 16px;
    padding: 1rem;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(179, 135, 65, 0.1);
  }

  .videos-grid-header {
    margin-bottom: 1rem;
    padding: 0 0.5rem;
  }

  .videos-grid-title {
    font-size: 1.8rem;
    color: #2c2f11;
    margin-bottom: 0.25rem;
    line-height: 1.2;
  }

  .videos-count {
    font-size: 0.9rem;
    color: #666;
    opacity: 0.8;
  }

  .videos-grid-fixed {
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;
    padding: 0.5rem;
  }

  .video-card {
    border-radius: 12px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(179, 135, 65, 0.15);
    backdrop-filter: blur(8px);
  }

  .video-thumbnail-container {
    border-radius: 10px 10px 0 0;
    overflow: hidden;
  }

  .video-info {
    padding: 0.8rem;
  }

  .video-title {
    font-size: 1rem;
    line-height: 1.3;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    color: #2c2f11;
  }

  .video-subtitle {
    font-size: 0.8rem;
    margin-top: 0.25rem;
    opacity: 0.8;
  }

  .videos-grid-slide {
    padding: 0.5rem;
  }

  .videos-slide-container {
    gap: 1rem;
    padding: 0.5rem 0;
  }

  .videos-grid-slide .video-card {
    flex: 0 0 calc(85% - 1rem);
  }

  .slide-nav-dupin {
    width: 32px;
    height: 32px;
    background: #2c2f11;
    border: 1px solid rgba(179, 135, 65, 0.2);
  }

  .slide-nav-dupin.prev {
    left: -8px;
  }

  .slide-nav-dupin.next {
    right: -8px;
  }

  .slide-nav-dupin svg {
    width: 20px;
    height: 20px;
  }

  /* Video Modal Mobile Styles */
  .video-modal-dupin-content {
    width: 95%;
    margin: 1rem;
    border-radius: 16px;
  }

  .video-modal-dupin-info {
    padding: 1rem;
  }

  .video-modal-dupin-title {
    font-size: 1.4rem;
    line-height: 1.3;
  }

  .video-modal-dupin-subtitle {
    font-size: 1rem;
    margin-top: 0.5rem;
  }

  .close-modal-dupin {
    top: 10px;
    right: 10px;
    width: 36px;
    height: 36px;
  }
}

/* Improve Mobile Touch Experience */
@media (hover: none) and (pointer: coarse) {
  .video-card {
    -webkit-tap-highlight-color: transparent;
  }

  .video-card:active {
    transform: scale(0.98);
  }

  .slide-nav-dupin {
    -webkit-tap-highlight-color: transparent;
    padding: 0.5rem;
  }

  .slide-nav-dupin:active {
    background: rgba(179, 135, 65, 0.3);
  }
}

/* No Results */
.no-results {
  grid-column: 1 / -1;
  text-align: center;
  padding: 4rem 1rem;
  background-color: rgba(44, 47, 17, 0.95);
  border-radius: 12px;
  border: 1px solid rgba(179, 135, 65, 0.2);
}

.no-results h3 {
  font-size: 1.8rem;
  margin-bottom: 1rem;
  color: #b38741;
  font-family: "Meholrax", serif;
}

.no-results p {
  opacity: 0.7;
  color: #fff;
  font-size: 1.1rem;
}

/* Video Modal */
.video-modal-dupin {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(44, 47, 17, 0.95);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  animation: modalFadeIn 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
}

@keyframes modalFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.video-modal-dupin-content {
  width: 90%;
  max-width: 1000px;
  position: relative;
  background-color: #2c2f11;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.7);
  animation: modalContentSlideIn 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  border: 1px solid rgba(179, 135, 65, 0.3);
}

@keyframes modalContentSlideIn {
  from {
    transform: translateY(50px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.close-modal-dupin {
  position: absolute;
  top: 20px;
  right: 20px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: rgba(44, 47, 17, 0.9);
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: #b38741;
  font-size: 24px;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10;
  transition: all 0.3s ease;
}

@media screen and (min-width: 769px) {
  .close-modal-dupin {
    top: 60px;
  }
}

.close-modal-dupin:hover {
  background-color: rgba(179, 135, 65, 0.2);
  transform: rotate(90deg);
}

.video-container {
  position: relative;
  padding-top: 56.25%;
  width: 100%;
}

.video-container iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: none;
}

.video-modal-dupin-info {
  padding: 2rem;
  padding-top: 50px;
  border-top: 1px solid rgba(179, 135, 65, 0.2);
}

.video-modal-dupin-header {
  display: flex;
  flex-wrap: wrap;
  align-items: flex-start;
  gap: 1rem;
  margin-bottom: 1.5rem;
}

.video-modal-dupin-title {
  width: 100%;
  font-size: 2rem;
  margin: 0;
  font-weight: 500;
  color: #b38741;
  font-family: "Meholrax", serif;
  letter-spacing: 0.5px;
}

.video-modal-dupin-subtitle {
  font-size: 1.2rem;
  opacity: 0.8;
  margin: 0;
  width: 100%;
  color: rgba(255, 255, 255, 0.8);
}

.video-modal-dupin-category {
  display: inline-block;
  background-color: rgba(179, 135, 65, 0.1);
  padding: 0.4rem 1rem;
  border-radius: 4px;
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #b38741;
}

.video-modal-dupin-duration {
  font-size: 1rem;
  opacity: 0.6;
  color: #b38741;
}

.video-modal-dupin-description {
  font-size: 1.1rem;
  line-height: 1.6;
  opacity: 0.8;
  margin: 0;
  color: rgba(255, 255, 255, 0.9);
}

/* Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

