/* Filter Menu Button */
.filter-menu-button {
  position: absolute;
  top: 20px;
  right: 20px;
  z-index: 10;
  display: flex;
  align-items: center;
  gap: 0.8rem;
  color: #fff;
  background: none;
  border: none;
  padding: 0.8rem 1.2rem;
  cursor: pointer;
  font-size: 1.5rem;
  transition: all 0.3s ease;
}

.filter-menu-button:hover {
  background: rgba(179, 135, 65, 0.2);
}

.filter-menu-text {
  font-family: "Meholrax", serif;
  display: block;
}

@media (max-width: 768px) {
  .filter-menu-text {
    display: none;
  }

  .filter-menu-button {
    padding: 0.8rem;
  }
}

/* Filter Sidebar */
.filter-sidebar {
  position: fixed;
  background: rgba(44, 47, 17, 0.98);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  z-index: 1000;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

@media (min-width: 769px) {
  .filter-sidebar {
    top: 0;
    right: -400px;
    width: 400px;
    height: 100vh;
    border-left: 1px solid rgba(179, 135, 65, 0.3);
  }

  .filter-sidebar.show {
    right: 0;
  }
}

@media (max-width: 768px) {
  .filter-sidebar {
    left: 0;
    right: 0;
    bottom: -100%;
    height: 90vh;
    border-radius: 20px 20px 0 0;
  }

  .filter-sidebar.show {
    bottom: 0;
  }
}

.filter-sidebar-content {
  height: 100%;
  overflow-y: auto;
  padding: 2rem;
}

/* Category List and Tag List Common Styles */
.category-list,
.tag-list {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1rem;
  margin-bottom: 2rem;
}

.category-item,
.tag-item {
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(179, 135, 65, 0.2);
  border-radius: 25px;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
  padding: 0.8rem 1.5rem;
  text-align: center;
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
}

.category-item:hover,
.tag-item:hover {
  background: rgba(179, 135, 65, 0.1);
  border-color: yellowgreen;
}

.category-item.active,
.tag-item.active {
  background: rgba(179, 135, 65, 0.2);
  border-color: yellowgreen;
}

/* Section Headers */
.filter-section h2 {
  color: yellowgreen;
  font-family: "Meholrax", serif;
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  text-align: left;
}

/* Close Button */
.close-sidebar {
  position: absolute;
  top: 1rem;
  right: 1rem;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: rgba(179, 135, 65, 0.1);
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: yellowgreen;
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;
}

.close-sidebar:hover {
  background: rgba(179, 135, 65, 0.2);
  transform: rotate(90deg);
}

/* Mobile Adjustments */
@media (max-width: 768px) {
  .filter-sidebar-content {
    padding: 1.5rem;
  }

  .category-list,
  .tag-list {
    gap: 0.8rem;
  }

  .category-item,
  .tag-item {
    padding: 0.6rem 1rem;
    font-size: 0.9rem;
  }

  .filter-section h2 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }
}

/* Filter Menu Button */
.filter-menu-button {
  position: absolute;
  top: 20px;
  right: 20px;
  z-index: 10;
  display: flex;
  align-items: center;
  gap: 0.8rem;
  color: #fff;
  background: none;
  border: none;
  padding: 0.8rem 1.2rem;
  cursor: pointer;
  font-size: 1.5rem;
  transition: all 0.3s ease;
}

.filter-menu-text {
  font-family: "Meholrax", serif;
  display: block;
  color: yellowgreen;
}

@media (max-width: 768px) {
  .filter-menu-text {
    display: none;
  }

  .filter-menu-button {
    padding: 0.8rem;
  }
}

/* Active Filter Indicator */
.active-filter {
  position: relative;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  z-index: 9;
  background: rgba(44, 47, 17, 0.95);
  padding: 0.6rem 1.2rem;
  border-radius: 25px;
  display: flex;
  align-items: center;
  gap: 0.8rem;
  font-family: "Roboto", sans-serif;
  color: #fff;
  border: 1px solid rgba(179, 135, 65, 0.2);
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.active-filter span {
  font-size: 0.95rem;
  font-weight: 400;
  letter-spacing: 0.3px;
}

.active-filter button {
  background: none;
  border: none;
  color: yellowgreen;
  font-size: 1rem;
  cursor: pointer;
  padding: 0.2rem 0.4rem;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  font-weight: 500;
}

.active-filter button:hover {
  opacity: 0.8;
  transform: scale(1.1);
}

@media (max-width: 768px) {
  .active-filter {
    top: auto;
    bottom: 20px;
    padding: 0.5rem 1rem;
    font-size: 0.9rem;
    background: rgba(44, 47, 17, 0.98);
  }

  .active-filter span {
    font-size: 0.9rem;
  }

  .active-filter button {
    font-size: 0.9rem;
  }
}
