.cp-categories-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
}

.cp-categories-header {
  text-align: center;
  margin-bottom: 4rem;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
}

.cp-categories-title {
  font-family: "Meholrax", serif;
  font-size: 3rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
  position: relative;
  text-transform: uppercase;
  background: linear-gradient(90deg, #fcfccc, #596016);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent;
}

.cp-categories-title::after {
  content: "";
  position: absolute;
  bottom: -20px;
  left: 50%;
  transform: translateX(-50%);
  width: 160px; /* Wider line */
  height: 2px;
  background-color: var(--primary-color); /* Using gold from variables */
  box-shadow: 0 0 10px rgba(179, 135, 65, 0.5); /* Subtle glow effect */
}

.cp-categories-description {
  font-family: "Roboto", sans-serif;
  font-size: 1.25rem; /* Slightly larger */
  line-height: 1.6;
  color: #e0e0e0; /* Lighter color for better contrast */
  margin-top: 3.5rem; /* More space */
  font-weight: 300; /* Lighter font weight */
}

/* Updated grid layout to match reference image */
.cp-categories-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr); /* 4 columns on desktop */
  gap: 25px; /* Larger gap between items */
  margin-top: 2.5rem;
  perspective: 1200px; /* Added for 3D effect */
}

/* Category card styling */
.cp-category-card {
  position: relative;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.4);
  cursor: pointer;
  transition: transform 0.4s cubic-bezier(0.215, 0.61, 0.355, 1),
    box-shadow 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
  height: 100%;
  border: 1px solid rgba(255, 255, 255, 0.05); /* Subtle border */
  opacity: 0;
  transform: translateY(30px);
  animation: categoryAppear 0.8s cubic-bezier(0.19, 1, 0.22, 1) forwards;
  will-change: transform, opacity;
  transform-style: preserve-3d;
}

.cp-category-card:nth-child(1) {
  animation-delay: 0.1s;
}

.cp-category-card:nth-child(2) {
  animation-delay: 0.15s;
}

.cp-category-card:nth-child(3) {
  animation-delay: 0.2s;
}

.cp-category-card:nth-child(4) {
  animation-delay: 0.25s;
}

.cp-category-card:nth-child(5) {
  animation-delay: 0.3s;
}

.cp-category-card:nth-child(6) {
  animation-delay: 0.35s;
}

.cp-category-card:nth-child(7) {
  animation-delay: 0.4s;
}

.cp-category-card:nth-child(8) {
  animation-delay: 0.45s;
}

@keyframes categoryAppear {
  from {
    opacity: 0;
    transform: translateY(50px) rotateX(10deg);
  }
  to {
    opacity: 1;
    transform: translateY(0) rotateX(0);
  }
}

.cp-category-card:hover {
  transform: translateY(-10px) scale(1.02); /* Slight scale effect */
  box-shadow: 0 20px 45px rgba(0, 0, 0, 0.5), 0 0 25px rgba(179, 135, 65, 0.2); /* Gold-tinted shadow on hover */
  z-index: 2;
}

.cp-category-image-container {
  position: relative;
  height: 0;
  padding-bottom: 100%; /* Maintain aspect ratio */
  overflow: hidden;
}

.cp-category-image {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.8s cubic-bezier(0.19, 1, 0.22, 1);
  filter: saturate(1.1) contrast(1.05); /* Subtle enhancement */
}

.cp-category-card:hover .cp-category-image {
  transform: scale(1.1);
}

.cp-category-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.2) 0%,
    rgba(0, 0, 0, 0.8) 100%
  );
  transition: opacity 0.4s ease;
}

.cp-category-card:hover .cp-category-overlay {
  opacity: 0.6; /* Reduced opacity on hover */
}

.cp-category-title {
