  font-size: 1.6rem;
  color: white;
  text-shadow: 0 2px 6px rgba(0, 0, 0, 0.7);
  transition: transform 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
}

.cp-category-card:hover .cp-category-title {
  transform: translateY(-10px);
}

