/* Diagonal section dividers between sections */
.diagonal-divider.white-to-dark {
  position: absolute;
  bottom: 0;
  left: 0;
  height: 120px;
  width: 100%;
  overflow: hidden;
  z-index: 5;
  transform: translateY(50%);
}

.diagonal-divider.dark-to-white {
  position: absolute;
  top: 0;
  left: 0;
  height: 120px;
  width: 100%;
  overflow: hidden;
  z-index: 5;
  transform: translateY(-50%);
}

.diagonal-divider.white-to-dark::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #ffffff;
  transform: skewY(2deg); /* More subtle angle */
  transform-origin: top right;
  box-shadow: 0 -5px 20px rgba(0, 0, 0, 0.08);
  transition: transform 0.3s ease;
}

.diagonal-divider.dark-to-white::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #000000;
  transform: skewY(2deg); /* More subtle angle */
  transform-origin: bottom left;
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;
}

/* Immersive Collection Highlights Section */
.cp-highlights-section {
  position: relative;
  padding: 10rem 0 4rem;
  background-color: #2c2f11;
  color: #ffffff;
  overflow: hidden;
  margin-top: -5rem;
  clip-path: polygon(0 3%, 100% 0, 100% 100%, 0 100%);
}

.cp-highlights-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
}

.cp-highlights-title {
