  margin-top: 3rem;
  margin-bottom: 4rem;
  font-weight: 300;
  text-align: center;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
}

/* Artistic asymmetric grid layout */
.cp-highlights-grid {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  grid-template-rows: auto auto;
  grid-auto-rows: minmax(300px, auto);
  align-items: center;
  margin: auto;
  gap: 25px;
  margin-top: 2.5rem;
}

.cp-highlight-item {
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.4);
  cursor: pointer;
  transition: transform 0.4s cubic-bezier(0.215, 0.61, 0.355, 1),
    box-shadow 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
  position: relative;
  border: 1px solid rgba(255, 255, 255, 0.05);
  opacity: 0;
  transform: translateY(30px);
  animation: highlightAppear 0.8s cubic-bezier(0.19, 1, 0.22, 1) forwards;
  will-change: transform, opacity;
  transform-style: preserve-3d;
  background-color: #0a0a0a;
  height: 100%;
}

/* Fixed grid positions for each highlight item */
.cp-highlight-item:nth-child(1) {
  grid-column: 1 / span 8;
  grid-row: 1;
  animation-delay: 0.1s;
}

.cp-highlight-item:nth-child(2) {
  grid-column: 9 / span 4;
  grid-row: 1;
  animation-delay: 0.15s;
}

.cp-highlight-item:nth-child(3) {
  grid-column: 1 / span 4;
  grid-row: 2;
  animation-delay: 0.2s;
}

.cp-highlight-item:nth-child(4) {
  grid-column: 5 / span 8;
  grid-row: 2;
  animation-delay: 0.25s;
}

.cp-highlight-item:nth-child(5) {
  grid-column: 1 / span 6;
  grid-row: 3;
  animation-delay: 0.3s;
}

.cp-highlight-item:nth-child(6) {
  grid-column: 7 / span 6;
  grid-row: 3;
  animation-delay: 0.35s;
}

.cp-highlight-item:nth-child(7) {
  grid-column: 1 / span 12;
  grid-row: 4;
  animation-delay: 0.4s;
}

@keyframes highlightAppear {
  from {
    opacity: 0;
    transform: translateY(50px) rotateX(10deg);
  }
  to {
    opacity: 1;
    transform: translateY(0) rotateX(0);
  }
}

.cp-highlight-item:hover {
