/* Enhanced Video Modal Styling */
.cp-video-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  opacity: 0;
  animation: videoModalFadeIn 0.6s cubic-bezier(0.19, 1, 0.22, 1) forwards;
  backdrop-filter: blur(10px);
  background: radial-gradient(
    circle at center,
    rgba(0, 0, 0, 0.9),
    rgba(0, 0, 0, 0.95)
  );
}

.cp-modal-content {
  position: relative;
  z-index: 1001;
  max-height: 90vh;
  overflow: auto;
}

.cp-video-modal.closing {
  animation: videoModalFadeOut 0.5s cubic-bezier(0.19, 1, 0.22, 1) forwards;
}

@keyframes videoModalFadeIn {
  from {
    opacity: 0;
    backdrop-filter: blur(0px);
  }
  to {
    opacity: 1;
    backdrop-filter: blur(10px);
  }
}

@keyframes videoModalFadeOut {
  from {
    opacity: 1;
    backdrop-filter: blur(10px);
  }
  to {
    opacity: 0;
    backdrop-filter: blur(0px);
  }
}

.cp-video-modal .cp-modal-content {
  width: 90%;
  max-width: 1200px;
  background-color: transparent;
  overflow: hidden;
  box-shadow: 0 30px 60px rgba(0, 0, 0, 0.5), 0 0 30px rgba(179, 135, 65, 0.2);
  transform: translateY(30px) scale(0.98);
  opacity: 0;
  animation: videoContentAppear 0.8s cubic-bezier(0.19, 1, 0.22, 1) 0.1s
    forwards;
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.08);
  position: relative;
  max-height: 90vh;
  display: flex;
  flex-direction: column;
}

@keyframes videoContentAppear {
  from {
