  opacity: 0.8;
  transform: scale(0);
  animation: closeButtonAppear 0.5s cubic-bezier(0.19, 1, 0.22, 1) 0.7s forwards;
  backdrop-filter: blur(5px);
  transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

@keyframes closeButtonAppear {
  from {
    transform: scale(0) rotate(-90deg);
    opacity: 0;
  }
  to {
    transform: scale(1) rotate(0deg);
    opacity: 0.8;
  }
}

.cp-video-modal .cp-modal-close:hover {
  background-color: rgba(179, 135, 65, 0.8);
  transform: scale(1.1) rotate(90deg);
  opacity: 1;
  box-shadow: 0 0 15px rgba(179, 135, 65, 0.4);
}

/* Loading Animation */
.cp-video-loading {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1;
}

.cp-video-loading-circle {
  width: 80px;
  height: 80px;
  border: 3px solid rgba(255, 255, 255, 0.1);
  border-top: 3px solid rgba(255, 255, 255, 0.8);
  border-radius: 50%;
  animation: videoLoading 1.2s cubic-bezier(0.5, 0, 0.5, 1) infinite;
}

@keyframes videoLoading {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.cp-video-related {
  margin-top: 25px;
  opacity: 0;
  animation: fadeIn 1s ease 0.9s forwards;
}

.cp-video-related-title {
  font-family: "Meholrax", serif;
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1rem;
}

.cp-video-related-items {
  display: flex;
  gap: 15px;
  overflow-x: auto;
  padding-bottom: 10px;
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.2) transparent;
}

.cp-video-related-items::-webkit-scrollbar {
  height: 5px;
}

.cp-video-related-items::-webkit-scrollbar-track {
  background: transparent;
}

.cp-video-related-items::-webkit-scrollbar-thumb {
  background-color: rgba(255, 255, 255, 0.2);
  border-radius: 10px;
}

.cp-video-related-item {
  flex: 0 0 120px;
  border-radius: 8px;
  overflow: hidden;
  cursor: pointer;
  transition: transform 0.3s ease;
  position: relative;
}

.cp-video-related-item:hover {
  transform: translateY(-5px);
}

.cp-video-related-item img {
  width: 100%;
  height: 70px;
  object-fit: cover;
}

.cp-video-related-item-title {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  font-weight: 600;
  margin-top: 0.5rem;
}

/* Artistic decorative elements */
.cp-video-decorative-element {
  position: absolute;
  opacity: 0.04;
  pointer-events: none;
}

.cp-video-decorative-element.top-left {
  top: -50px;
  left: -50px;
  width: 200px;
  height: 200px;
  background: radial-gradient(
    circle,
    rgba(255, 255, 255, 0.8) 0%,
    transparent 70%
  );
  animation: pulse 8s infinite alternate;
}

.cp-video-decorative-element.bottom-right {
  bottom: -50px;
  right: -50px;
  width: 150px;
  height: 150px;
  background: radial-gradient(
    circle,
    rgba(179, 135, 65, 0.8) 0%,
    transparent 70%
  );
  animation: pulse 6s infinite alternate-reverse;
}

@keyframes pulse {
  0% {
    transform: scale(1);
    opacity: 0.02;
  }
  100% {
    transform: scale(1.5);
    opacity: 0.06;
  }
}

/* More specific responsive adjustments for video modal */
@media (max-width: 768px) {
  .cp-video-modal .cp-modal-content {
    width: 95%;
  }

  .cp-modal-video-title {
    font-size: 1.6rem;
  }

  .cp-modal-video-description {
    font-size: 1rem;
  }

  .cp-modal-video-details {
    padding: 20px;
  }

  .cp-video-modal .cp-modal-close {
    width: 38px;
    height: 38px;
  }
}

@media (max-width: 576px) {
  .cp-modal-video-header {
    flex-direction: column;
    align-items: flex-start;
  }

  .cp-modal-video-category {
    margin-top: 10px;
  }

  .cp-modal-video-title {
    font-size: 1.3rem;
  }

  .cp-video-related-items {
    gap: 10px;
  }

  .cp-video-related-item {
    flex: 0 0 100px;
  }
}

/* Enhanced Video Highlights Item Effects */
.cp-highlight-item[class*="video-item"] {
  overflow: hidden;
  transition: transform 0.5s cubic-bezier(0.19, 1, 0.22, 1),
    box-shadow 0.5s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-highlight-item[class*="video-item"]::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(
    circle at center,
    rgba(179, 135, 65, 0.2) 0%,
    transparent 70%
  );
  opacity: 0;
  z-index: 2;
  transition: opacity 0.8s ease;
  pointer-events: none;
}

.cp-highlight-item[class*="video-item"]:hover::before {
  opacity: 1;
}

.cp-highlight-item[class*="video-item"] .cp-highlight-img {
  transition: transform 0.8s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-highlight-item[class*="video-item"]:hover .cp-highlight-img {
  transform: scale(1.08);
}

.cp-highlight-item[class*="video-item"] .cp-play-button {
  transition: transform 0.6s cubic-bezier(0.19, 1, 0.22, 1),
    opacity 0.6s cubic-bezier(0.19, 1, 0.22, 1);
  box-shadow: 0 0 0 rgba(255, 255, 255, 0.3);
}

.cp-highlight-item[class*="video-item"]:hover .cp-play-button {
  transform: translate(-50%, -50%) scale(1.15);
  animation: pulse-glow 2s infinite;
}

@keyframes pulse-glow {
  0% {
    box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.5);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(255, 255, 255, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
  }
}

.cp-highlight-item[class*="video-item"] .cp-play-circle {
  transition: all 0.5s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-highlight-item[class*="video-item"]:hover .cp-play-circle {
  fill: rgba(179, 135, 65, 0.7);
  stroke-width: 3;
  stroke: rgba(255, 255, 255, 0.9);
}

.cp-highlight-item[class*="video-item"] .cp-play-triangle {
  transition: transform 0.5s cubic-bezier(0.19, 1, 0.22, 1),
    fill 0.5s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-highlight-item[class*="video-item"]:hover .cp-play-triangle {
  transform: scale(1.2) translateX(1px);
  fill: rgba(255, 255, 255, 1);
}

.cp-highlight-item[class*="video-item"] .cp-highlight-gradient {
  transition: opacity 0.6s cubic-bezier(0.19, 1, 0.22, 1),
    background 0.6s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-highlight-item[class*="video-item"]:hover .cp-highlight-gradient {
  background: linear-gradient(
    to top,
    rgba(0, 0, 0, 0.85) 0%,
    rgba(0, 0, 0, 0.4) 50%,
    rgba(0, 0, 0, 0.2) 100%
  );
}

/* Video opening animation */
.cp-highlight-item.video-opening {
  animation: videoItemOpen 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards;
}

@keyframes videoItemOpen {
  0% {
    transform: scale(1);
    opacity: 1;
  }
  100% {
    transform: scale(1.05);
    opacity: 0.6;
  }
}

/* Enhanced video indicator */
.cp-video-indicator {
  font-family: "Roboto", sans-serif;
  font-size: 11px;
  color: white;
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-top: 8px;
  background-color: rgba(0, 0, 0, 0.5);
  padding: 4px 12px;
  border-radius: 20px;
  font-weight: 500;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
  transition: all 0.3s ease;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.cp-highlight-item[class*="video-item"]:hover .cp-video-indicator {
  background-color: rgba(179, 135, 65, 0.7);
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
}

/* Improve play button presentation */
.cp-play-button {
  display: flex;
  flex-direction: column;
  align-items: center;
  filter: drop-shadow(0 5px 15px rgba(0, 0, 0, 0.5));
}

.cp-play-icon {
  margin-bottom: 10px;
  filter: drop-shadow(0 2px 5px rgba(0, 0, 0, 0.5));
}

/* Video opening animation and effects */
.cp-highlight-item.video-opening {
  animation: videoItemOpen 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards;
  z-index: 10;
}

@keyframes videoItemOpen {
  0% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.95;
  }
  100% {
    transform: scale(1.02);
    opacity: 0.5;
  }
}

@keyframes ripple-out {
  0% {
    transform: translate(-50%, -50%) scale(0);
    opacity: 0.6;
  }
  100% {
    transform: translate(-50%, -50%) scale(2.5);
    opacity: 0;
  }
}

/* Video item hover animations */
.cp-highlight-item[class*="video-item"] {
  transform: translateZ(0);
  backface-visibility: hidden;
  perspective: 1000px;
  will-change: transform, box-shadow;
  overflow: hidden;
  transition: transform 0.5s cubic-bezier(0.19, 1, 0.22, 1),
    box-shadow 0.5s cubic-bezier(0.19, 1, 0.22, 1),
    opacity 0.5s cubic-bezier(0.19, 1, 0.22, 1);
}

.cp-modal-video {
  display: flex;
  flex-direction: column;
  max-height: 90vh;
  overflow: hidden;
}

.cp-modal-video-details {
  overflow-y: auto;
  max-height: 35vh;
}

/* Height-based responsive adjustments for video modal */
@media (max-height: 800px) {
  .cp-video-modal .cp-modal-content {
    max-height: 95vh;
  }

  .cp-video-container {
    padding-bottom: 52%; /* Slightly smaller aspect ratio for shorter screens */
  }

  .cp-modal-video-details {
    max-height: 30vh;
    padding: 20px;
  }

  .cp-modal-video-title {
    font-size: 1.8rem;
  }
}

@media (max-height: 600px) {
  .cp-video-container {
    padding-bottom: 45%; /* Even smaller aspect ratio for very short screens */
  }

  .cp-modal-video-details {
    max-height: 25vh;
    padding: 15px;
  }

  .cp-modal-video-title {
    font-size: 1.5rem;
  }

  .cp-video-related {
    display: none; /* Hide related videos on very small screens */
  }
}

/* Mobile performance optimizations */
@media (max-width: 768px) {
  /* Optimize for smooth scrolling on mobile */
  .collection-page {
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch; /* For smoother inertial scrolling on iOS */
  }

  /* Force hardware acceleration on key elements */
  .cp-hero,
  .cp-discover-section,
  .cp-categories-section,
  .cp-featured-artwork-section,
  .cp-highlights-section,
  .diagonal-divider {
    transform: translateZ(0);
    backface-visibility: hidden;
    will-change: transform;
  }

  /* Reduce motion for elements that can cause jank */
  .cp-featured-image,
  .cp-discover-artwork-item img,
  .cp-category-image {
    transition: transform 0.3s ease;
  }

  /* Simplify transitions that are expensive to render */
  .cp-category-card:hover,
  .cp-highlight-item:hover {
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.2);
  }

  /* Optimize diagonal dividers that cause most of the performance issues */
  .diagonal-divider.white-to-black::before,
  .diagonal-divider.black-to-white::before,
  .diagonal-divider.white-to-dark::before,
  .diagonal-divider.dark-to-white::before,
  .diagonal-divider.white-to-white::before {
    transform: skewY(-8deg) translateZ(0);
    box-shadow: none;
    will-change: transform;
  }
}

/* Extra optimizations for very small screens */
@media (max-width: 576px) {
  /* Further reduce animation complexity on small screens */
  .cp-highlight-item[class*="video-item"]:hover .cp-play-button {
    transform: translate(-50%, -50%) scale(1.05);
    animation: none;
  }

  /* Disable complex hover effects on mobile */
  .cp-category-card::after,
  .cp-category-artwork-card::after {
    display: none;
  }

  /* Simplify the diagonal dividers further */
  .diagonal-divider {
    height: 50px;
  }

  /* Remove perspective effects on highlights grid */
  .cp-highlight-item {
    transform: none !important;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }
}

/* Optimize diagonal divider rendering for mobile */
@media (max-width: 768px) {
  /* Replace transform-heavy dividers with simpler alternatives on mobile */
  .diagonal-divider {
    position: absolute;
    width: 100%;
    height: 60px; /* Shorter height on mobile */
    overflow: hidden;
    z-index: 5;
    pointer-events: none;
  }

  .diagonal-divider.top {
    top: -1px; /* Ensure no gap at top */
  }

  .diagonal-divider.bottom {
    bottom: -1px; /* Ensure no gap at bottom */
  }

  /* Use a more performant approach on mobile devices */
  .diagonal-divider.white-to-black::before,
  .diagonal-divider.black-to-white::before,
  .diagonal-divider.white-to-dark::before,
  .diagonal-divider.dark-to-white::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 200%; /* Taller to account for rotation */
    background-color: inherit;
    transform: skewY(-6deg);
    transform-origin: top left;
    box-shadow: none; /* Remove expensive shadows */
    -webkit-transform: skewY(-6deg) translate3d(0, 0, 0); /* iOS optimization */
    -webkit-backface-visibility: hidden; /* Prevent flickering */
    perspective: 1000px;
  }

  /* Provide specific backgrounds instead of inherit */
  .diagonal-divider.white-to-black::before {
    background-color: #ffffff;
  }

  .diagonal-divider.black-to-white::before {
    background-color: #131313;
  }

  /* Fix for Safari/iOS rendering issues */
  .cp-categories-section,
  .cp-highlights-section {
    z-index: 1;
    isolation: isolate;
  }
}

/* Further simplify for even smaller screens */
@media (max-width: 576px) {
  .diagonal-divider {
    height: 50px;
    transform: none; /* Remove transform from container */
  }

  /* Use a steeper angle but even simpler transforms */
  .diagonal-divider.white-to-black::before,
  .diagonal-divider.black-to-white::before,
  .diagonal-divider.white-to-dark::before,
  .diagonal-divider.dark-to-white::before {
    transform: skewY(-8deg);
    -webkit-transform: skewY(-8deg) translate3d(0, 0, 0);
  }

  /* Apply static positions to reduce layout calculations */
  .cp-categories-section .diagonal-divider.top {
    top: -1px;
  }

  .cp-highlights-section .diagonal-divider.top {
    top: -1px;
  }
}

/* Hero Navigation Controls */
.cp-hero-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
