@font-face {
  font-family: "Mythical-Prince";
  src: url("../../../assets/font/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: "louvreserif";
  src: url("../../../assets/font/louvreserif/400/latin.woff2")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}
/* Container styles */
.packages-container {
  width: 100%;
  min-height: 100vh;
  position: relative;
  overflow: hidden;
}

/* Hero section */
.packages-hero {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
}

.hero-image-container {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.hero-image {
  position: absolute;
  width: 100%;
  height: 100%;
  object-fit: cover;
  transform: scale(1.1);
  transition: all 1.5s cubic-bezier(0.165, 0.84, 0.44, 1);
  filter: brightness(0.85);
}

.hero-image.portrait {
  width: auto;
  height: 100%;
  max-width: none;
  object-fit: contain;
  object-position: center;
}

.hero-image.landscape {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.hero-image.square {
  width: 100%;
  height: 100%;
  object-fit: contain;
  max-height: 100vh;
}

/* Hero Content */
.packages-hero-content {
  position: absolute;
  top: 80%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: #fff;
  z-index: 2;
  width: 90%;
  max-width: 1200px;
}

.packages-hero::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0) 0%,
    rgba(0, 0, 0, 0.3) 100%
  );
  z-index: 1;
}

.packages-title {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 2.4rem;
  color: #fff;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5);
  opacity: 1;
  transform: translateY(30px);
  animation: fadeInUp 0.8s ease forwards;
}

.packages-subtitle {
  font-size: 1.2rem;
  line-height: 1.6;
  max-width: 800px;
  margin: 0 auto;
  color: rgba(255, 255, 255, 0.9);
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 0.8s ease 0.2s forwards;
}

/* Content section */
.packages-content {
  position: relative;
  width: 100%;
  margin: 0 auto;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  overflow: hidden;
}

.packages-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 10px;
  padding-top: 10px;
  opacity: 0;
  transform: translateY(30px);
  animation: fadeInUp 0.8s ease forwards;
}

/* Package cards */
.visit-cardss {
  position: relative;
  border-radius: 5px;
  overflow: hidden;
  cursor: pointer;
  width: 100%;
  height: 100%;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.visit-cardss:hover {
  transform: translateY(-10px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
}

.visit-cards-img-wrap {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.visit-cards-img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.visit-cards-img.portrait {
  height: 100%;
  width: 100%;
  max-width: none;
  object-fit: contain;
}

.visit-cards-img.landscape {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.visit-cards-img.square {
  width: 100%;
  height: 100%;
  object-fit: contain;
}

.visit-cards:hover .visit-cards-img {
  transform: scale(1.1);
}

.visit-cards-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0),
    rgba(0, 0, 0, 0.2) 30%,
    rgba(0, 0, 0, 0.5)
  );
  opacity: 0.9;
  transition: opacity 0.3s ease;
}

.visit-cards:hover .visit-cards-overlay {
  opacity: 1;
}

.visit-card-content {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  color: #fff;
  z-index: 2;
  transform: translateY(10px);
  transition: transform 0.4s ease;
}

.visit-cards:hover .visit-card-content {
  transform: translateY(0);
}

.visit-card-content h2 {
  font-family: "Meholrax", serif;
  font-size: 1.3rem;
  font-weight: 700;
  margin-bottom: 5px;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.visit-card-content p {
  font-size: 0.9rem;
  line-height: 1.6;
  opacity: 0.9;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

