/* Image wrapper and styling */
.collection-image-wrapper {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
  border-radius: 4px;
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.12);
  transition: all 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  transform-origin: center bottom;
  backface-visibility: hidden; /* Prevent flickering on hover */
}

/* Museum frame effect */
.collection-image-wrapper::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.15);
  z-index: 2;
  pointer-events: none;
  transition: all 0.5s ease;
}

.collection-image-wrapper:hover {
  transform: translateY(-8px) scale(1.02);
  box-shadow: 0 12px 24px rgba(0, 0, 0, 0.18);
  z-index: 10;
}

.collection-image-wrapper:hover::before {
  box-shadow: inset 0 0 25px rgba(0, 0, 0, 0.2);
}

.collection-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.7s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  backface-visibility: hidden; /* Prevent flickering during animation */
}

.collection-image-wrapper:hover .collection-image {
  transform: scale(1.08);
}

/* Light reflection effect */
.collection-image-wrapper::after {
  content: "";
  position: absolute;
  top: -100%;
  left: -100%;
  width: 50%;
  height: 200%;
  background: linear-gradient(
    to right,
    rgba(255, 255, 255, 0) 0%,
    rgba(255, 255, 255, 0.3) 50%,
    rgba(255, 255, 255, 0) 100%
  );
  transform: rotate(30deg);
  transition: transform 0.7s ease;
  z-index: 3;
  pointer-events: none;
  opacity: 0;
}

.collection-image-wrapper:hover::after {
  transform: rotate(30deg) translate(300%, 0);
  opacity: 1;
}

/* Overlay for image info */
.collection-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background: linear-gradient(
