/* Wavy Gallery Layout */
.wavy-gallery .collection-inner-container {
  align-items: flex-start; /* Allow items to have different vertical positions */
  padding: 40px 5px; /* More space for vertical movement */
  transition: transform 0.5s ease;
}

.wavy-gallery .collection-item {
  transition: transform 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94),
    height 0.3s ease, width 0.3s ease, filter 0.5s ease, scale 0.3s ease;
  margin-right: 25px; /* More spacing between items */
  transform-origin: center center;
  z-index: 1;
}

.wavy-gallery .collection-item:hover {
  transform: translateY(-8px) scale(1.05) !important;
  z-index: 10;
  filter: brightness(1.05);
}

/* Animation effect for items when hovered */
.wavy-gallery .collection-item:hover ~ .collection-item {
  transform: translateX(10px);
}

/* Improved shadow effects for wavy layout */
.wavy-gallery .collection-image-wrapper {
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  transition: box-shadow 0.5s ease,
    transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.wavy-gallery .collection-image-wrapper:hover {
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.25);
}

