/* Homestay Details Sidebar styling */
.homestay-details-sidebar {
  position: fixed;
  top: 0;
  right: -100%;
  width: 450px;
  max-width: 100%;
  height: 100vh;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  z-index: 1000;
  transition: right 0.5s cubic-bezier(0.19, 1, 0.22, 1);
  overflow-y: auto;
  padding: 0;
  display: flex;
  flex-direction: column;
  color: #1e293b;
  font-family: "Roboto", sans-serif;
  scrollbar-width: thin;
  scrollbar-color: #cbd5e1 #f8fafc;
}

.homestay-details-sidebar::-webkit-scrollbar {
  width: 6px;
}

.homestay-details-sidebar::-webkit-scrollbar-track {
  background: #f8fafc;
}

.homestay-details-sidebar::-webkit-scrollbar-thumb {
  background-color: #cbd5e1;
  border-radius: 10px;
}

.homestay-details-sidebar.open {
  right: 0;
}

.details-sidebar-header {
  padding: 22px 25px !important;
  background: #2c2f11;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.details-sidebar-header h2 {
  margin: 0;
  color: white;
  font-family: "Meholrax", serif;
  font-weight: 600;
  font-size: 1.6rem;
  background: linear-gradient(90deg, #fcfccc, #a8b530);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent;
  position: relative;
}

.details-sidebar-header h2::after {
  content: "";
  position: absolute;
  bottom: -8px;
  left: 0;
  width: 40px;
  height: 3px;
  background: linear-gradient(
    to right,
    rgba(255, 255, 255, 0.8),
    rgba(255, 255, 255, 0.2)
  );
  border-radius: 2px;
}

.details-sidebar-header .close-sidebar {
  width: 38px;
  height: 38px;
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(4px);
  border: none;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
}

.details-sidebar-header .close-sidebar:hover {
  background: rgba(255, 255, 255, 0.25);
  transform: rotate(90deg);
}

/* Enhanced Gallery Styles */
.homestay-gallery {
  position: relative;
  margin-bottom: 25px;
}

.gallery-main {
  position: relative;
  height: 280px;
  overflow: hidden;
  margin-bottom: 10px;
}

.gallery-main img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.7s cubic-bezier(0.19, 1, 0.22, 1);
}

.gallery-main:hover img {
  transform: scale(1.05);
}

.gallery-thumbnails {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 10px;
  padding: 0 25px;
  margin-bottom: 20px;
}

.gallery-thumbnail {
  height: 70px;
  border-radius: 8px;
  overflow: hidden;
  cursor: pointer;
  position: relative;
  box-shadow: 0 3px 10px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
}

.gallery-thumbnail img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: all 0.3s ease;
}

.gallery-thumbnail::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(15, 23, 42, 0.3);
  opacity: 1;
  transition: opacity 0.3s ease;
  z-index: 1;
}

.gallery-thumbnail:hover::before {
  opacity: 0;
}

.gallery-thumbnail:hover {
  transform: translateY(-3px);
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.15);
}

.gallery-zoom-icon {
  position: absolute;
  bottom: 15px;
  right: 15px;
  background: rgba(0, 0, 0, 0.6);
  color: #fff;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 5;
  backdrop-filter: blur(4px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s ease;
  opacity: 0.7;
}

.gallery-main:hover .gallery-zoom-icon {
  opacity: 1;
  transform: scale(1.1);
  bottom: 20px;
}

