/* FAQ Section Styles */
.faq-section {
  position: relative;
  padding: 80px 0;
  overflow: hidden;
  margin-top: -30px;
}

.faq-container {
  position: relative;
  max-width: 900px;
  margin: 0 auto;
  padding: 0 10px;
  z-index: 1;
}

.faq-header {
  text-align: center;
  margin-bottom: 60px;
  color: #000000;
}

/* Enhanced artistic FAQ title */
.faq-title {
  font-family: "Meholrax", serif;
  font-size: 3rem;
  font-weight: 600;
  line-height: 1.2;
  position: relative;
  text-transform: uppercase;
  margin-bottom: 2.5rem;
  letter-spacing: 0.05em;
}

.faq-title::before {
  content: "";
  position: absolute;
  width: 90px;
  height: 90px;
  border-radius: 50%;
  background: radial-gradient(
    circle,
    rgba(30, 58, 138, 0.08) 0%,
    rgba(30, 58, 138, 0) 70%
  );
  top: -25px;
  left: 50%;
  transform: translateX(-50%);
  z-index: -1;
  animation: faqTitleGlow 7s infinite alternate;
}

.faq-title::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 160px;
  height: 2px;
  background: linear-gradient(90deg, transparent, #1e3a8a, transparent);
  border-radius: 3px;
  transition: width 0.6s cubic-bezier(0.19, 1, 0.22, 1);
}

.faq-title span {
  position: relative;
  z-index: 2;
  display: inline-block;
}

.faq-title span::before,
.faq-title span::after {
  content: "✦";
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  font-size: 1rem;
  color: rgba(30, 58, 138, 0.4);
}

.faq-title span::before {
  left: -3rem;
}

.faq-title span::after {
  right: -3rem;
}

.faq-header:hover .faq-title::after {
  width: 240px;
}

@keyframes faqTitleGlow {
  0% {
    opacity: 0.4;
    transform: translateX(-50%) scale(1);
    box-shadow: 0 0 20px rgba(30, 58, 138, 0);
  }
  100% {
    opacity: 0.8;
    transform: translateX(-50%) scale(1.5);
    box-shadow: 0 0 30px rgba(30, 58, 138, 0.1);
  }
}

.faq-subtitle {
  font-family: "Roboto", sans-serif;
  font-size: 1.2rem;
  font-weight: 400;
  margin-bottom: 2rem;
}

.faq-list {
  display: flex;
  flex-direction: column;
  gap: 25px;
}

.faq-item {
  border-radius: 16px;
  overflow: hidden;

  transition: all 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
  border: 1px solid rgba(226, 232, 240, 0.8);
  position: relative;
}

.faq-item:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.1);
  border-color: rgba(226, 232, 240, 0.9);
}

.faq-question {
  padding: 25px 30px;
  padding-right: 60px;
  font-family: "Roboto", sans-serif;
  font-size: 1.2rem;
  font-weight: 600;
  color: #0f172a;
  cursor: pointer;
  position: relative;
  display: flex;
  align-items: center;
  user-select: none;
}

.faq-question::before {
  content: "";
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 4px;
  height: 0;
  background: linear-gradient(to bottom, #2563eb, #1e3a8a);
  border-radius: 2px;
  transition: height 0.3s ease;
}

.faq-item:hover .faq-question::before {
  height: 30px;
}

.faq-item.active .faq-question::before {
  height: 50px;
}

.faq-question::after {
  content: "+";
  position: absolute;
  right: 30px;
  top: 50%;
  transform: translateY(-50%);
  font-size: 1.8rem;
  color: #2c2f11;
  transition: all 0.3s ease;
}

.faq-item.active .faq-question::after {
  transform: translateY(-50%) rotate(45deg);
  color: #000000;
}

.faq-item.active {
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.12);
}

.faq-answer {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);
  padding: 0 30px;
  font-family: "Roboto", sans-serif;
  color: #000000;
  line-height: 1.8;
  font-size: 1rem;
}

.faq-item.active .faq-answer {
  max-height: 500px;
  padding-bottom: 30px;
}

.faq-answer p {
  margin-top: 0;
  margin-bottom: 15px;
}

.faq-answer p:last-child {
  margin-bottom: 0;
}

.faq-answer a {
  color: #2563eb;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease;
  position: relative;
}

.faq-answer a::after {
  content: "";
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 0;
  height: 1px;
  background: #2563eb;
  transition: width 0.3s ease;
}

.faq-answer a:hover::after {
  width: 100%;
}

.faq-category-tabs {
  display: flex;
  justify-content: center;
  margin-bottom: 40px;
  flex-wrap: wrap;
  gap: 15px;
}

.faq-category-tab {
  padding: 12px 25px;
  font-family: "Roboto", sans-serif;
  font-size: 0.95rem;
  font-weight: 600;
  color: #64748b;
  background: #f8fafc;
  border: 1px solid #e2e8f0;
  border-radius: 30px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.faq-category-tab:hover {
  transform: translateY(-3px);
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  color: #2563eb;
  border-color: #e0e7ff;
}

.faq-category-tab.active {
  background: linear-gradient(135deg, #1e3a8a, #2563eb);
  color: white;
  border: none;
  box-shadow: 0 5px 15px rgba(37, 99, 235, 0.2);
}

.faq-item .tag {
  padding: 5px 12px;
  font-size: 0.7rem;
  font-weight: 600;
  border-radius: 20px;
  display: inline-block;
  margin-right: 10px;
  background: #f1f5f9;
  color: #64748b;
}

.faq-item .tag.popular {
  background: #eef2ff;
