}

.faq-footer {
  text-align: center;
  margin-top: 60px;
  padding: 30px;
  border-radius: 16px;
  border: 1px solid #e2e8f0;
}

.faq-footer-title {
  font-family: "Meholrax", serif;
  font-size: 2rem;
  margin-bottom: 1rem;
  font-weight: 600;
}

.faq-footer-text {
