/* About Page Styles */
.about-page {
  width: 100%;
  overflow-x: hidden;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  z-index: 0;
  min-height: 100vh;
  font-family: "Roboto", sans-serif;
  position: relative;
  color: var(--text-color);
}

.about-page::before {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

/* Navigation Sidebar */
.about-nav {
  position: fixed;
  right: 40px;
  top: 50%;
  transform: translateY(-50%);
  z-index: 100;
  background: rgba(44, 47, 17, 0.8);
  backdrop-filter: blur(10px);
  border-radius: 30px;
  padding: 20px 10px;
  box-shadow: 0 4px 30px rgba(179, 135, 65, 0.1);
  border: 1px solid rgba(179, 135, 65, 0.1);
}

.about-nav ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.about-nav button {
  background: none;
  border: none;
  color: #fff;
  padding: 15px 25px;
  margin: 5px 0;
  cursor: pointer;
  font-size: 14px;
  position: relative;
  transition: all 0.3s ease;
  opacity: 0.7;
  font-family: "Meholrax", serif;
  letter-spacing: 1px;
  width: 100%;
  text-align: right;
}

.about-nav button::after {
  content: "";
  position: absolute;
  right: -10px;
  top: 50%;
  transform: translateY(-50%);
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background-color: #b38741;
  opacity: 0;
  transition: all 0.3s ease;
  box-shadow: 0 0 10px #b38741;
}

.about-nav button:hover,
.about-nav button.active {
  opacity: 1;
  color: #b38741;
  transform: translateX(-5px);
}

.about-nav button.active::after {
  opacity: 1;
  right: -5px;
}

/* Hero Section */
.about-heros {
  position: relative;
  overflow: hidden;
  margin-bottom: 30px;
}

.heros-video-container {
  width: 100%;
  height: 100%;
  position: relative;
}

.heros-video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  opacity: 0;
  transition: opacity 1.5s ease;
  transform: scale(1.1);
}

.heros-video.loaded {
  opacity: 1;
  transform: scale(1);
}

