}

.flip-card {
  background-color: transparent;
  width: 280px;
  height: 350px;
  perspective: 1400px;
  position: relative;
  cursor: pointer;
  transition: transform 0.3s ease;
}

.flip-card:hover {
  transform: scale(1.02);
}

.flip-card-inner {
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.8s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  transform-style: preserve-3d;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.flip-card:hover .flip-card-inner {
  transform: rotateY(180deg);
}

.flip-card-front,
.flip-card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.4);
}

.flip-card-front {
  background: linear-gradient(
    135deg,
    rgba(179, 135, 65, 0.1),
    rgba(44, 47, 17, 0.1)
  );
  transform: rotateY(0deg);
}

.flip-card-back {
  background: linear-gradient(
    135deg,
    rgba(44, 47, 17, 0.1),
    rgba(179, 135, 65, 0.1)
  );
  transform: rotateY(180deg);
}

.flip-card-front img,
.flip-card-back img {
  width: 100%;
  height: 100%;
  object-fit: contain;
  background: #f5f5f5;
  object-position: center;
  transition: transform 0.8s cubic-bezier(0.19, 1, 0.22, 1);
  filter: saturate(1.1) contrast(1.05);
}

.flip-card:hover .flip-card-front img {
  transform: scale(1.1);
}

.flip-card:hover .flip-card-back img {
  transform: scale(1.05);
}

.card-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(
    to top,
    rgba(0, 0, 0, 0.8) 0%,
    rgba(0, 0, 0, 0.4) 50%,
    transparent 100%
  );
  color: white;
  padding: 20px 15px 15px;
  transform: translateY(10px);
  opacity: 0;
  transition: all 0.6s cubic-bezier(0.19, 1, 0.22, 1);
}

.flip-card:hover .card-overlay {
  transform: translateY(0);
  opacity: 1;
}

.card-overlay h3 {
  font-family: "Meholrax", serif;
  font-size: 1.1rem;
  font-weight: 600;
  margin: 0 0 8px 0;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.7);
