}

.card-overlay p {
  font-family: "Roboto", sans-serif;
  font-size: 0.85rem;
  margin: 0;
  opacity: 0.9;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
  letter-spacing: 0.5px;
}

/* Decorative elements */
.flip-card::before {
  content: "";
  position: absolute;
  top: -10px;
  left: -10px;
  right: -10px;
  bottom: -10px;
  background: linear-gradient(
    45deg,
    rgba(179, 135, 65, 0.2),
    rgba(44, 47, 17, 0.2)
  );
  border-radius: 25px;
  z-index: -1;
  opacity: 0;
  transition: opacity 0.6s ease;
  transform: rotate(-1deg);
}

.flip-card:hover::before {
  opacity: 1;
}

.flip-card::after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.9);
  border-radius: 50%;
  transform: translate(-50%, -50%) scale(0);
  transition: transform 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
