.acoustic-detail-container {
  width: 100%;
  min-height: 100vh;
  background: #2c2f11;
  color: #fff;
}

/* Hero Section */
.acoustic-detail-hero {
  position: relative;
  width: 100%;
  height: 90vh;
  overflow: hidden;
}

.hero-slideshow {
  position: relative;
  width: 100%;
  height: 100%;
}

.hero-slide {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}

.hero-slide.active {
  opacity: 1;
}

.acoustic-detail-hero-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.hero-dots {
  position: absolute;
  bottom: 60px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 10px;
  z-index: 10;
}

.hero-dot {
  width: 40px;
  height: 6px;
  background: rgba(255, 255, 255, 0.3);
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.hero-dot:hover {
  background: rgba(255, 255, 255, 0.5);
}

.hero-dot.active {
  background: #fff;
  width: 50px;
}

.acoustic-detail-hero-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.2),
    rgba(0, 0, 0, 0.4)
  );
  z-index: 1;
}

.acoustic-detail-hero-content {
  position: relative;
  z-index: 3;
  padding: 0 5vw 3vw 3vw;
  margin-bottom: 10%;
}

.acoustic-detail-hero-content h1 {
  font-family: "Mythical-Prince", "louvreserif", "Times New Roman", Times, serif;
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 1.2rem;
  letter-spacing: 2px;
  color: #fff;
  text-shadow: 0 4px 24px rgba(0, 0, 0, 0.5);
}

.acoustic-detail-hero-content p {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  color: #e0e0e0;
  line-height: 1.6;
  margin: 0;
  text-shadow: 0 2px 12px rgba(0, 0, 0, 0.4);
}

.back-button {
  position: absolute;
  top: 2rem;
  left: 2rem;
  z-index: 3;
  background: rgba(0, 0, 0, 0.5);
  border: none;
  color: #fff;
  padding: 0.8rem 1.5rem;
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  transition: all 0.3s ease;
}

.back-button:hover {
  background: rgba(0, 0, 0, 0.8);
  transform: translateX(-5px);
}

.back-button svg {
  width: 20px;
  height: 20px;
}

/* Content Section */
.acoustic-detail-content {
  padding: 4rem 5vw;
  background: #2c2f11;
}

.content-wrapper-acoustic {
  max-width: 1200px;
  margin: 0 auto;
}

.content-wrapper-acoustic h2 {
  font-family: "Meholrax", serif;
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  color: #fff;
}

.content-wrapper-acoustic p {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  line-height: 1.8;
  color: #e0e0e0;
  white-space: pre-line;
  margin-bottom: 1.5rem;
}

.content-wrapper-acoustic p:last-child {
  margin-bottom: 0;
}

/* Slideshow Section */
.acoustic-detail-slideshow {
  padding: 2rem 0;
  background: #2c2f11;
}

.slideshow-container {
  position: relative;
  width: 100%;
  height: 70vh;
  overflow: hidden;
}

.slides-wrapper {
  display: flex;
  transition: transform 0.5s ease;
  height: 100%;
}

.slide {
  min-width: 100%;
  height: 100%;
  position: relative;
  cursor: pointer;
}

.slide img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.play-button {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: #fff;
  opacity: 0.8;
  transition: opacity 0.3s ease;
}

.slide:hover .play-button {
  opacity: 1;
}

.slide-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: rgba(0, 0, 0, 0.5);
  border: none;
  color: #fff;
  padding: 1rem;
  cursor: pointer;
  z-index: 2;
  transition: background 0.3s ease;
}

.slide-nav:hover {
  background: rgba(0, 0, 0, 0.8);
}

.slide-nav.prev {
  left: 1rem;
}

.slide-nav.next {
  right: 1rem;
}

.slide-indicators {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
  position: relative;
}

.slide-count {
  position: absolute;
  right: 1rem;
  font-family: "Roboto", sans-serif;
  font-size: 0.9rem;
  color: #fff;
  background: rgba(0, 0, 0, 0.5);
  padding: 0.3rem 0.8rem;
  border-radius: 20px;
}

.indicator {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  border: none;
  cursor: pointer;
  transition: background 0.3s ease;
}

.indicator.active {
  background: #fff;
}

/* Video Modal */
.video-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(0, 0, 0, 0.9);
  z-index: 1000;
  display: flex;
  justify-content: center;
  align-items: center;
  opacity: 0;
  animation: fadeIn 0.3s ease forwards;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.video-container {
  width: 100%;
  height: 100%;
  position: relative;
}

.close-modal {
  position: absolute;
  top: 2rem;
  right: 2rem;
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
  z-index: 1001;
  padding: 0.5rem;
  transition: transform 0.3s ease;
}

.close-modal:hover {
  transform: scale(1.1);
}

/* Responsive Design */
@media (max-width: 768px) {
  .acoustic-detail-hero-content h1 {
    font-size: 1.8rem;
  }

  .slideshow-container {
    height: 50vh;
  }

  .hero-nav {
    width: 35px;
    height: 35px;
  }

  .hero-dot {
    width: 30px;
    height: 3px;
  }

  .hero-dot.active {
    width: 40px;
  }
}

.hero-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(8px);
  border: none;
  border-radius: 50%;
  width: 45px;
  height: 45px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 10;
}

.hero-nav:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateY(-50%) scale(1.1);
}
