@font-face {
  font-family: "Mythical-Prince";
  src: url("../../assets/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

.visitor-rules-container {
  width: 100%;
  max-width: 100%;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
}

/* Banner styling */
.rules-banner {
  position: relative;
  width: 100%;
  height: 300px;
  overflow: hidden;
}

.rules-banner img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: brightness(0.6);
  transition: transform 0.5s ease-in-out;
}

.rules-banner:hover img {
  transform: scale(1.05);
}

.banner-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  text-align: center;
  z-index: 2;
}

.banner-overlay h1 {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 3.5rem;
  color: white;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3);
  font-weight: 600;
  margin: 0;
}

/* Content styling */
.rules-content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  border-radius: 5px;
  margin-top: 30px;
  position: relative;
  z-index: 3;
  padding-top: 100px;
}

.section {
  margin-bottom: 2.5rem;
  padding: 1.5rem;
  border-radius: 10px;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.section:hover {
  transform: translateY(-5px);
  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
}

.section h2 {
  font-size: 1.8rem;
  color: #333;
  margin-bottom: 1.5rem;
  font-family: "Meholrax", serif;
  position: relative;
  padding-bottom: 0.5rem;
}

.section h2::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 50px;
  height: 2px;
  background: #000;
}

.section p {
  line-height: 1.8;
  color: #555;
  margin-bottom: 1.5rem;
}

.section ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.section ul li {
  margin-bottom: 1rem;
  padding-left: 2rem;
  position: relative;
  line-height: 1.6;
  color: #444;
}

.section ul li::before {
  content: "â€¢";
  position: absolute;
  left: 0;
  color: #000;
  font-size: 1.5rem;
  line-height: 1;
}

/* Mobile responsiveness */
@media screen and (max-width: 768px) {
  .rules-banner {
    height: 200px;
  }

  .banner-overlay h1 {
    font-size: 2.2rem;
  }

  .rules-content {
    padding: 1.5rem;
    margin-top: -30px;
  }

  .section {
    padding: 1rem;
    margin-bottom: 1.5rem;
  }

  .section h2 {
    font-size: 1.5rem;
  }

  .section ul li {
    padding-left: 1.5rem;
  }
}

@media screen and (max-width: 480px) {
  .rules-banner {
    height: 150px;
  }

  .banner-overlay h1 {
    font-size: 1.8rem;
  }

  .rules-content {
    padding: 1rem;
    margin-top: 25px;
  }

  .section {
    padding: 0.8rem;
  }

  .section h2 {
    font-size: 1.3rem;
  }

  .section p,
  .section ul li {
    font-size: 0.95rem;
  }
}

/* Animation for sections */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.section {
  animation: fadeInUp 0.5s ease forwards;
  opacity: 0;
}

.section:nth-child(1) {
  animation-delay: 0.1s;
}
.section:nth-child(2) {
  animation-delay: 0.2s;
}
.section:nth-child(3) {
  animation-delay: 0.3s;
}
.section:nth-child(4) {
  animation-delay: 0.4s;
}
.section:nth-child(5) {
  animation-delay: 0.5s;
}
.section:nth-child(6) {
  animation-delay: 0.6s;
}
.section:nth-child(7) {
  animation-delay: 0.7s;
}
