@font-face {
  font-family: "Mythical-Prince";
  src: url("../../../assets/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

.legal-notice-container {
  width: 100%;
  max-width: 100%;
  overflow-x: hidden;
}

/* Banner styling */
.legal-banner {
  position: relative;
  width: 100%;
  height: 300px;
  overflow: hidden;
}

.legal-banner img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: brightness(0.6);
  transition: transform 0.5s ease-in-out;
}

.legal-banner:hover img {
  transform: scale(1.05);
}

.banner-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  text-align: center;
  z-index: 2;
}

.banner-overlay h1 {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 3.5rem;
  color: white;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3);
  font-weight: 600;
  margin: 0;
}

/* Content styling */
.legal-content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  overflow-x: hidden;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  border-radius: 5px;
  margin-top: 0px;
  position: relative;
  z-index: 3;
}

.section {
  margin-bottom: 2.5rem;
}

.section h2 {
  font-size: 1.8rem;
  color: #333;
  margin-bottom: 1rem;
  border-bottom: 2px solid #f0f0f0;
  padding-bottom: 0.5rem;
}

.section h3 {
  font-size: 1.4rem;
  color: #444;
  margin: 1.5rem 0 0.8rem;
}

.section p {
  line-height: 1.6;
  margin-bottom: 1rem;
  color: #555;
}

.subsection {
  margin-left: 1.5rem;
  margin-top: 1rem;
  margin-bottom: 1.5rem;
}

.subsection h4 {
  font-size: 1.2rem;
  color: #555;
  margin-bottom: 0.8rem;
}

.subsection ul {
  margin-left: 1.5rem;
  margin-bottom: 1rem;
}

.subsection li {
  margin-bottom: 0.5rem;
  line-height: 1.5;
}

/* Highlight important information */
.highlight {
  padding: 1rem;
  border-left: 4px solid #007bff;
  margin: 1rem 0;
}

/* Link styling */
.section a {
  color: #007bff;
  text-decoration: none;
  transition: color 0.3s ease;
}

.section a:hover {
  color: #0056b3;
  text-decoration: underline;
}

/* Enhanced mobile responsiveness */
@media screen and (max-width: 768px) {
  .legal-banner {
    height: 200px;
    overflow: hidden;
  }

  .legal-banner img {
    filter: brightness(0.5) contrast(1.1);
    animation: subtleZoom 20s infinite alternate;
  }

  @keyframes subtleZoom {
    0% {
      transform: scale(1);
    }
    100% {
      transform: scale(1.1);
    }
  }

  .banner-overlay h1 {
    font-size: 2.2rem;
    margin-bottom: 1rem;
    text-shadow: 2px 2px 10px rgba(0, 0, 0, 0.7);
    letter-spacing: 0.05em;
    position: relative;
    display: inline-block;
  }

  .banner-overlay h1::after {
    content: "";
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 50px;
    height: 2px;
    background: linear-gradient(90deg, transparent, white, transparent);
  }


  .section {
    position: relative;
    padding: 0.5rem 0;
    margin-bottom: 2rem;
    transition: transform 0.3s ease;
  }

  .section:hover {
    transform: translateY(-3px);
  }

  .section::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 3px;
    height: 0;
    background: linear-gradient(to bottom, #007bff, transparent);
    transition: height 0.3s ease;
  }

  .section:hover::before {
    height: 100%;
  }

  .section h2 {
    font-size: 1.5rem;
    padding-left: 1rem;
  }

  .section h3 {
    font-size: 1.2rem;
    margin: 1.5rem 0 0.8rem;
    color: #555;
  }

  .section p {
    padding-left: 1rem;
    border-left: 1px solid #f0f0f0;
    margin-left: 0.5rem;
  }
}

@media screen and (max-width: 480px) {
  .legal-banner {
    height: 150px;
  }

  .banner-overlay h1 {
    font-size: 1.8rem;
  }

  .banner-overlay h1::after {
    width: 40px;
    height: 1px;
  }


  .section h2 {
    font-size: 1.3rem;
    padding-left: 0.5rem;
    letter-spacing: 0.03em;
  }

  .section h3 {
    font-size: 1.1rem;
    margin: 1.2rem 0 0.7rem;
  }

  .subsection {
    margin-left: 0.5rem;
  }

  .section p {
    font-size: 0.95rem;
    line-height: 1.5;
    padding-left: 0.5rem;
  }

  /* Creative on-scroll animation for sections */
  .section {
    opacity: 0.8;
    transform: translateY(10px);
    transition: opacity 0.5s ease, transform 0.5s ease;
  }

  .section:hover {
    opacity: 1;
    transform: translateY(0);
  }

  /* Add a subtle design element to lists */
  ul {
    list-style-type: none;
  }

  ul li {
    position: relative;
    padding-left: 1rem;
    margin-bottom: 0.7rem;
  }

  ul li::before {
    content: "â†’";
    position: absolute;
    left: 0;
    color: #007bff;
    font-size: 0.8rem;
  }
}
