transforms.result:
.theacoustic-container {
  width: 100%;
  min-height: 100vh;
  background: #2c2f11;
  color: #fff;
}

.theacoustic-hero {
  position: relative;
  font-family: "Mythical-Prince", "louvreserif", "Times New Roman", Times, serif;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: flex-end;
  justify-content: flex-start;
}

.theacoustic-hero-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
}

.theacoustic-hero-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0) 0%,
    rgba(0, 0, 0, 0.85) 100%
  );
  z-index: 2;
}

.theacoustic-card-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0) 0%,
    rgba(0, 0, 0, 0.5) 100%
  );
  z-index: 2;
}

.theacoustic-hero-content {
  position: relative;
  z-index: 3;
  padding: 0 5vw 3vw 3vw;
  margin-bottom: 10%;
}

.theacoustic-hero-content h1 {
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 1.2rem;
  letter-spacing: 2px;
  color: #fff;
  text-shadow: 0 4px 24px rgba(0, 0, 0, 0.5);
}

.theacoustic-hero-content p {
  font-size: 1rem;
  color: #e0e0e0;
  font-family: "Roboto", sans-serif;
  line-height: 1.6;
  margin: 0;
  text-shadow: 0 2px 12px rgba(0, 0, 0, 0.4);
}

.theacoustic-cards {
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin: 20px auto 0 auto;
  padding-left: 10px;
  padding-right: 10px;
}

.theacoustic-card {
  display: flex;
  flex-direction: row;
  background: none;
  box-shadow: none;
  overflow: hidden;
  gap: 0;
  border-radius: 0;
  padding: 0;
  position: relative;
  border-radius: 10px;
}

.theacoustic-card:hover {
  transform: scale(1.02);
}

.theacoustic-card-img-wrap {
  position: relative;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: stretch;
  justify-content: center;
}

.theacoustic-card-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  display: block;
}

.theacoustic-card-content {
  position: absolute;
  top: 35%;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  justify-content: center;
  z-index: 2;
  color: #fff;
  text-align: center;
  background: none;
}

.theacoustic-card-content h2 {
  font-size: 2.2rem;
  font-weight: 700;
  font-family: "Meholrax", serif;
  color: #fff;
  letter-spacing: 1.2px;
  text-shadow: 0 4px 24px rgba(0, 0, 0, 0.7), 0 1px 0 #222;
  border-bottom: none;
  margin-bottom: 0;
  padding-bottom: 0;
  padding-left: 30px;
  padding-right: 30px;
}

.theacoustic-card-content p {
  font-size: 1rem;
  color: #f3f3f3;
  line-height: 1.2;
  padding-left: 30px;
  padding-right: 30px;
  font-family: "Roboto", sans-serif;
  text-shadow: 0 2px 12px rgba(0, 0, 0, 0.6);
}
