.block-content-exhibition {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/background/Background2.webp");
  background-repeat: repeat;
  background-position: top center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  padding: 80px 20px;
}

.exhibition-container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.exhibition-image-wrapper {
  width: 100%;
  max-width: 600px;
  margin-bottom: 40px;
}

.exhibition-image {
  width: 100%;
  height: auto;
  object-fit: cover;
  aspect-ratio: 3/4;
}

.exhibition-content {
  max-width: 800px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
}

.exhibition-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  color: #000;
  margin: 0;
}

.exhibition-subtitle {
  font-family: "Roboto", sans-serif;
  font-size: 1.2rem;
  color: #333;
  margin: 0;
}

.exhibition-description {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  line-height: 1.6;
  color: #666;
  max-width: 700px;
  margin: 0;
}

.exhibition-button {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  color: #fff;
  background-color: #2c2f11;
  border: 1px solid #fff;
  padding: 15px 30px;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 20px;
}

.exhibition-button:hover {
  background-color: #3a3e17;
}

/* Responsive styles */
@media (max-width: 768px) {
  .block-content-exhibition {
    padding: 60px 15px;
  }

  .exhibition-image-wrapper {
    max-width: 400px;
  }

  .exhibition-title {
    font-size: 2rem;
  }

  .exhibition-subtitle {
    font-size: 1.1rem;
  }

  .exhibition-description {
    font-size: 0.9rem;
    padding: 0 20px;
  }
}

@media (max-width: 480px) {
  .block-content-exhibition {
    padding: 40px 10px;
  }

  .exhibition-image-wrapper {
    max-width: 300px;
  }

  .exhibition-title {
    font-size: 1.8rem;
  }

  .exhibition-subtitle {
    font-size: 1rem;
  }

  .exhibition-button {
    padding: 12px 25px;
    font-size: 0.9rem;
  }
}
