.package-detail-container {
  width: 100%;
  min-height: 100vh;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  overflow: hidden;
  position: relative;
}

.package-detail-hero {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #000;
}

.package-detail-hero-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.package-detail-hero-img.portrait {
  width: auto;
  height: 100%;
  max-height: 100vh;
  margin: 0 auto;
}

.package-detail-hero-img.landscape {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.package-detail-hero-img.square {
  width: auto;
  height: 100%;
  max-height: 100vh;
  margin: 0 auto;
}

.package-detail-hero-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.2) 0%,
    rgba(0, 0, 0, 0.6) 100%
  );
  z-index: 1;
}

.package-detail-hero-content {
  position: absolute;
  bottom: 10%;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 1200px;
  color: #fff;
  text-align: left;
  z-index: 2;
  padding: 0 40px;
}

.package-detail-hero-content h1 {
  font-family: "Mythical-Prince", "louvreserif", "Times New Roman", Times, serif;
  font-size: 3.5rem;
  margin-bottom: 1.5rem;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 0.8s ease forwards;
}

.package-detail-hero-content p {
  font-size: 1.4rem;
  line-height: 1.6;
  max-width: 800px;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 0.8s ease 0.2s forwards;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Media Queries */
@media (max-width: 1200px) {
  .package-detail-hero-content {
    padding: 0 30px;
  }

  .package-detail-hero-content h1 {
    font-size: 3rem;
  }

  .package-detail-hero-content p {
    font-size: 1.3rem;
  }
}

@media (max-width: 768px) {
  .package-detail-hero-content {
    padding: 0 20px;
    bottom: 15%;
  }

  .package-detail-hero-content h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }

  .package-detail-hero-content p {
    font-size: 1.1rem;
  }

  .package-detail-hero-img.portrait {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
}

@media (max-width: 480px) {
  .package-detail-hero-content h1 {
    font-size: 2rem;
  }

  .package-detail-hero-content p {
    font-size: 1rem;
  }
}

.back-button2 {
  position: absolute;
  top: 30px;
  left: 30px;
  z-index: 10;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(8px);
  border: none;
  border-radius: 50%;
  color: #fff;
  width: 45px;
  height: 45px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 5px;
  cursor: pointer;
  transition: all 0.3s ease;
  padding: 0 15px;
  font-size: 0.9rem;
}

.back-button2:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.1);
}

.back-button2 svg {
  width: 24px;
  height: 24px;
  fill: currentColor;
}

.package-detail-content {
  padding: 60px 40px;
  position: relative;
}

.content-wrapper {
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: start;
}

.content-left {
  padding-right: 40px;
}

.content-right {
  padding-left: 40px;
  border-left: 1px solid rgba(0, 0, 0, 0.1);
}

.content-wrapper h2 {
  font-family: "Meholrax", serif;
  font-size: 2rem;
  border-bottom: none !important;
  margin-bottom: 2rem;
  color: #2c2f11;
  position: relative;
  padding-bottom: 15px;
}

.content-wrapper h2::after {
  content: "";
  position: absolute;
  bottom: 0;
  left: 0;
  width: 60px;
  height: 2px;
  background: #2c2f11;
}

.package-details-list {
  list-style: none;
  padding: 0;
  color: #000;
  margin-bottom: 3rem;
}

.package-details-list li {
  font-size: 1.1rem;
  line-height: 1.8;
  margin-bottom: 1.5rem;
  padding-left: 1.5rem;
  position: relative;
}

.package-details-list li::before {
  content: " ";
}

.package-details-list li::marker {
  color: #2c2f11 !important;
}

.package-price-info {
  margin-bottom: 3rem;
  background: rgba(44, 47, 17, 0.05);
  padding: 30px;
  border-radius: 15px;
}

.package-price-info .price {
  font-size: 2rem;
  color: #2c2f11;
  margin-bottom: 1rem;
  font-family: "Roboto", sans-serif;
}

.package-price-info .child-price {
  font-size: 1.8rem;
  color: #2c2f11;
  margin-bottom: 1rem;
  opacity: 0.8;
}

.package-price-info .time,
.package-price-info .schedule,
.package-price-info .note {
  font-size: 1.1rem;
  margin: 0.8rem 0;
  color: #2c2f11;
  line-height: 1.6;
}

.package-price-info strong {
  color: #2c2f11;
  font-weight: 600;
  margin-right: 0.5rem;
}

.package-price-info .note {
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid rgba(44, 47, 17, 0.1);
}

.package-video-section {
  margin-top: 3rem;
}

.video-thumbnail-wrapper {
  position: relative;
  width: 100%;
  padding-top: 56.25%;
  border-radius: 15px;
  overflow: hidden;
  cursor: pointer;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;
}

.video-thumbnail-wrapper:hover {
  transform: translateY(-5px);
}

.video-thumbnail {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.play-button-overlay {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  transition: transform 0.3s ease;
  background: rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(8px);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.play-button-overlay svg {
  width: 30px;
  height: 30px;
  fill: #fff;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
}

/* Modal styles */
.video-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.video-modal.active {
  opacity: 1;
  visibility: visible;
}

.modal-content {
  position: relative;
  width: 90%;
  max-width: 1200px;
  aspect-ratio: 16/9;
}

.close-modal2 {
  position: relative;
  top: 20px;
  left: 300px;
  right: 0px;
  width: 40px;
  height: 40px;
  border-radius: 10%;
  background: rgba(255, 255, 255, 0.2);
  border: 2px solid rgba(255, 255, 255, 0.3);
  color: #fff;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  z-index: 1001;
}
@media (min-width: 769px) {
  .close-modal2 {
     top: 50px;
    left: 60rem;
  }
}

.close-modal2:hover {
  background: rgba(255, 255, 255, 0.3);
  transform: scale(1.1) rotate(90deg);
}

.close-modal2 svg {
  width: 24px;
  height: 24px;
  fill: #fff;
}

@media (max-width: 1024px) {
  .content-wrapper {
    grid-template-columns: 1fr;
  }

  .content-left,
  .content-right {
    padding: 0;
  }

  .content-right {
    border-left: none;
    border-top: 1px solid rgba(0, 0, 0, 0.1);
  }
}

@media (max-width: 768px) {
  .package-detail-content {
    padding: 40px 20px;
  }

  .play-button-overlay {
    width: 60px;
    height: 60px;
  }

  .close-modal {
    top: -50px;
    right: 0;
  }
}

@media (max-width: 480px) {
  .package-price-info .price {
    font-size: 1.4rem;
  }

  .package-price-info .child-price {
    font-size: 1.5rem;
  }
}

.package-not-found {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  text-align: center;
  padding: 20px;
  color: #2c2f11;
}

.package-not-found h1 {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  margin-bottom: 1rem;
}

.package-not-found p {
  font-size: 1.2rem;
  opacity: 0.8;
}

/* Book Now Button */
.book-now-button {
  position: absolute;
  top: -30px;
  right: 30px;
  padding: 15px 30px;
  background: #2c2f11;
  color: white;
  border: none;
  border-radius: 30px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  z-index: 10;
}

.book-now-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.3);
  background: #3d4016;
}

/* Booking Modal Styles */
.booking-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(8px);
  display: none;
  justify-content: center;
  align-items: center;
  z-index: 2000;
  padding: 20px;
}

.booking-modal.active {
  display: flex;
  animation: modalFadeIn 0.4s ease;
}

.booking-modal .booking-modal-container {
  background: #fff;
  border-radius: 20px;
  width: 100%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
}

.booking-modal-header {
  background: linear-gradient(135deg, #2c2f11 0%, #3d4016 100%);
  padding: 30px;
  border-radius: 20px 20px 0 0;
  position: relative;
  overflow: hidden;
}

.booking-modal-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp")
    repeat;
  background-size: 150px;
  opacity: 0.1;
}

.booking-modal-header h2 {
  color: white;
  margin: 0;
  font-size: 2rem;
  font-weight: 600;
  text-align: center;
  font-family: "Meholrax", serif;
  position: relative;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
}

.booking-modal-content {
  padding: 40px;
}

.booking-package-info {
  background: linear-gradient(to right bottom, #f8fafc, #f1f5f9);
  border-radius: 15px;
  padding: 25px;
  margin-bottom: 35px;
  border: 1px solid #e2e8f0;
  position: relative;
  overflow: hidden;
}

.booking-package-info::before {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  width: 150px;
  height: 150px;
  background: linear-gradient(
    135deg,
    rgba(44, 47, 17, 0.1) 0%,
    transparent 100%
  );
  border-radius: 0 15px 0 100%;
}

.booking-price-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
  padding-bottom: 15px;
  border-bottom: 1px dashed #e2e8f0;
}

.booking-price-row:last-child {
  border-bottom: none;
  margin-bottom: 0;
  padding-bottom: 0;
}

.price-label {
  color: #64748b;
  font-weight: 500;
  font-size: 1.1rem;
}

.price-value {
  color: #2c2f11;
  font-weight: 700;
  font-size: 1.2rem;
  font-family: "Meholrax", serif;
}

.booking-note {
  margin-top: 20px;
  padding-top: 20px;
  border-top: 1px dashed #e2e8f0;
  color: #64748b;
  font-size: 0.95rem;
  font-style: italic;
  line-height: 1.6;
}

.booking-form.modern {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 25px;
}

.booking-form.modern .form-group.full-width {
  grid-column: 1 / -1;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.form-group label {
  color: #2c2f11;
  font-weight: 600;
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 8px;
}

.form-group label svg {
  width: 18px;
  height: 18px;
  opacity: 0.8;
}

.form-group input,
.form-group select,
.form-group textarea {
  padding: 14px 18px;
  border: 2px solid #e2e8f0;
  border-radius: 12px;
  font-size: 1rem;
  transition: all 0.3s ease;
  background: #fff;
  color: #0f172a;
}

.form-group input:hover,
.form-group select:hover,
.form-group textarea:hover {
  border-color: #cbd5e1;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
  border-color: #2c2f11;
  box-shadow: 0 0 0 4px rgba(44, 47, 17, 0.1);
  outline: none;
}

.form-group.has-error input,
.form-group.has-error select,
.form-group.has-error textarea {
  border-color: #ef4444;
  background-color: #fef2f2;
}

.error-message {
  color: #ef4444;
  font-size: 0.85rem;
  margin-top: 4px;
  display: flex;
  align-items: center;
  gap: 6px;
}

.error-message svg {
  width: 14px;
  height: 14px;
}

.btn-submit-booking {
  grid-column: 1 / -1;
  background: linear-gradient(135deg, #2c2f11 0%, #3d4016 100%);
  color: white;
  padding: 16px;
  border: none;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 12px;
  position: relative;
  overflow: hidden;
}

.btn-submit-booking::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to right,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  transform: translateX(-100%);
  transition: transform 0.6s ease;
}

.btn-submit-booking:hover:not(:disabled)::before {
  transform: translateX(100%);
}

.btn-submit-booking:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(44, 47, 17, 0.2);
}

.btn-submit-booking:disabled {
  background: #94a3b8;
  cursor: not-allowed;
}

.booking-close-button {
  position: absolute;
  top: 0px;
  right: 0px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  border: 2px solid rgba(255, 255, 255, 0.3);
  color: #fff;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  z-index: 1;
}

.booking-close-button:hover {
  background: rgba(255, 255, 255, 0.3);
  transform: rotate(90deg);
}

.booking-close-button svg {
  width: 24px;
  height: 24px;
  fill: currentColor;
}

.booking-spinner {
  width: 22px;
  height: 22px;
  border: 3px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top-color: white;
  animation: spin 1s linear infinite;
}

@media (max-width: 768px) {
  .booking-modal-content {
    padding: 25px;
  }

  .booking-form.modern {
    grid-template-columns: 1fr;
    gap: 20px;
  }

  .booking-modal-header {
    padding: 25px;
  }

  .booking-modal-header h2 {
    font-size: 1.6rem;
  }
}

@media (max-width: 480px) {
  .booking-modal-content {
    padding: 20px;
  }

  .booking-package-info {
    padding: 20px;
  }

  .booking-modal-header {
    padding: 20px;
  }

  .booking-modal-header h2 {
    font-size: 1.4rem;
  }

  .price-label,
  .price-value {
    font-size: 1rem;
  }
}

@keyframes modalFadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* Success Modal Styles */
.success-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(8px);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2100;
  animation: modalFadeIn 0.5s ease;
}

.success-modal-content {
  background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
  border-radius: 30px;
  padding: 40px;
  width: 90%;
  max-width: 500px;
  text-align: center;
  position: relative;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  transform: translateY(20px);
  animation: modalSlideUp 0.6s ease forwards;
  overflow: hidden;
}

.success-modal-content::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp")
    repeat;
  background-size: 150px;
  opacity: 0.05;
  z-index: 0;
}

.success-icon-wrapper {
  width: 100px;
  height: 100px;
  margin: 0 auto 30px;
  background: linear-gradient(135deg, #2c2f11 0%, #3d4016 100%);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  animation: iconPop 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s both;
}

.success-icon-wrapper::before {
  content: "";
  position: absolute;
  width: calc(100% + 20px);
  height: calc(100% + 20px);
  border: 2px solid #2c2f11;
  border-radius: 50%;
  animation: ripple 1.5s ease-out infinite;
}

.success-icon {
  width: 50px;
  height: 50px;
  fill: white;
  transform: scale(0);
  animation: iconScale 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.4s
    forwards;
}

.success-title {
  color: #2c2f11;
  font-size: 2rem;
  font-family: "Meholrax", serif;
  margin-bottom: 20px;
  position: relative;
  z-index: 1;
}

.success-message {
  color: #64748b;
  font-size: 1.1rem;
  line-height: 1.6;
  margin-bottom: 30px;
  position: relative;
  z-index: 1;
}

.btn-close-success {
  background: linear-gradient(135deg, #2c2f11 0%, #3d4016 100%);
  color: white;
  border: none;
  padding: 15px 40px;
  border-radius: 25px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  z-index: 1;
}

.btn-close-success:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(44, 47, 17, 0.2);
}

.btn-close-success::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to right,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  transform: translateX(-100%);
  transition: transform 0.6s ease;
}

.btn-close-success:hover::before {
  transform: translateX(100%);
}

@keyframes modalSlideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes iconPop {
  from {
    transform: scale(0);
  }
  to {
    transform: scale(1);
  }
}

@keyframes iconScale {
  to {
    transform: scale(1);
  }
}

@keyframes ripple {
  0% {
    transform: scale(1);
    opacity: 0.5;
  }
  100% {
    transform: scale(1.5);
    opacity: 0;
  }
}

@media (max-width: 768px) {
  .success-modal-content {
    padding: 30px;
  }

  .success-icon-wrapper {
    width: 80px;
    height: 80px;
  }

  .success-icon {
    width: 40px;
    height: 40px;
  }

  .success-title {
    font-size: 1.6rem;
  }

  .success-message {
    font-size: 1rem;
  }
}

/* Hero Slideshow Styles */
.hero-slideshow-container {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
}

.hero-slides {
  position: relative;
  width: 100%;
  height: 100%;
}

.hero-slide {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}

.hero-slide.active {
  opacity: 1;
  z-index: 1;
}

.hero-slide-image {
  width: 100%;
  height: 100%;
  object-fit: contain;
}

.hero-slide-image.landscape {
  object-fit: cover;
}

.hero-slide-image.portrait {
  width: auto;
  height: 100%;
  margin: 0 auto;
  display: block;
}

.hero-slide-image.square {
  width: auto;
  height: 100%;
  margin: 0 auto;
  display: block;
}

.slide-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(8px);
  border: none;
  border-radius: 50%;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
  transition: all 0.3s ease;
}

.slide-nav:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateY(-50%) scale(1.1);
}

.slide-nav.prev {
  left: 20px;
}

.slide-nav.next {
  right: 20px;
}

.slide-nav svg {
  width: 24px;
  height: 24px;
  fill: currentColor;
}

.slide-dots {
  position: absolute;
  bottom: 50px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 10px;
  z-index: 10;
}

.slide-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  border: 2px solid rgba(255, 255, 255, 0.5);
  cursor: pointer;
  transition: all 0.3s ease;
  padding: 0;
}

.slide-dot.active {
  background: white;
  transform: scale(1.2);
}

.hero-content {
  position: absolute;
  bottom: 10%;
  left: 50%;
  transform: translateX(-50%);
  width: 100%;
  max-width: 1200px;
  color: #fff;
  text-align: left;
  z-index: 10;
  padding: 0 20px;
}

.bg-hero-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.2) 0%,
    rgba(0, 0, 0, 0.6) 100%
  );
  z-index: 1;
}

.hero-content h1 {
  font-family: "Mythical-Prince", "louvreserif", "Times New Roman", Times, serif;
}

@media (max-width: 768px) {
  .slide-nav {
    width: 40px;
    height: 40px;
  }

  .slide-nav svg {
    width: 20px;
    height: 20px;
  }

  .slide-dots {
    bottom: 80px;
  }

  .slide-dot {
    width: 10px;
    height: 10px;
  }

  .hero-content {
    bottom: 25%;
  }
}

.slideshow-control {
  position: absolute;
  bottom: 50px;
  left: 20px;
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(8px);
  border: none;
  border-radius: 50%;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
  transition: all 0.3s ease;
}

.slideshow-control:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.1);
}

.slideshow-control svg {
  width: 16px;
  height: 16px;
  fill: currentColor;
}

@media (max-width: 768px) {
  .slideshow-control {
    bottom: 80px;
    width: 35px;
    height: 35px;
  }

  .slideshow-control svg {
    width: 14px;
    height: 14px;
  }
}
