/* MuseumMapPage.css */
.museum-map-page {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  color: #333;
  min-height: 100vh;
  font-family: "Roboto", sans-serif;
}

/* Hero Section */
.map-hero {
  position: relative;
  width: 100%;
  height: 50vh;
  overflow: hidden;
}

.map-hero-image {
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
}

.map-hero-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.3),
    rgba(0, 0, 0, 0.7)
  );
}

.map-back-button {
  position: absolute;
  top: 20px;
  left: 20px;
  color: white;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 16px;
  z-index: 10;
  background-color: rgba(0, 0, 0, 0.5);
  padding: 8px 15px;
  border-radius: 20px;
  transition: background-color 0.3s ease;
}

.map-back-button:hover {
  background-color: rgba(0, 0, 0, 0.7);
  color: white;
}

.map-hero-content {
  position: absolute;
  bottom: 40px;
  left: 20px;
  z-index: 10;
  color: white;
}

.map-hero-subtitle {
  font-size: 14px;
  letter-spacing: 1px;
  margin-bottom: 10px;
  opacity: 0.9;
  font-weight: 500;
}

.map-hero-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  font-weight: 600;
  margin: 0;
  line-height: 1.2;
}

/* Navigation Tabs */
.map-nav-tabs {
  display: flex;
  background-color: #fff;
  border-bottom: 1px solid #e0e0e0;
  position: sticky;
  top: 0;
  z-index: 100;
  flex-wrap: wrap;
}

.map-tab {
  flex: 1;
  min-width: 120px;
  background: none;
  border: none;
  padding: 12px 16px;
  font-size: 16px;
  font-weight: 500;
  color: #666;
  cursor: pointer;
  transition: all 0.3s ease;
  border-bottom: 3px solid transparent;
  white-space: normal;
  text-align: center;
  line-height: 1.2;
  height: auto;
  min-height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.map-tab:hover {
  color: #333;
  background-color: #f8f8f8;
}

.map-tab.active {
  color: #9acd32;
  border-bottom-color: #9acd32;
  background-color: #fff;
}

/* Content */
.map-content {
  min-height: 60vh;
}

.map-content-section {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

/* Notice */
.map-notice {
  border-left: 4px solid #2196f3;
  padding: 15px 20px;
  margin-bottom: 30px;
  border-radius: 4px;
}

.map-notice p {
  margin: 0;
  color: #1976d2;
  font-weight: 500;
}

/* Getting Here Section */
.map-getting-here-section {
  margin-bottom: 30px;
}

.map-section-title {
  font-size: 18px;
  font-weight: 600;
  color: #333;
  font-family: "Meholrax", serif;
  margin-bottom: 15px;
  letter-spacing: 0.5px;
}

.map-section-title.green {
  color: #9acd32;
}

.map-section-description {
  color: #666;
  line-height: 1.6;
  margin-bottom: 30px;
}

/* Transport Sections */
.map-transport-section {
  margin-bottom: 30px;
  padding-bottom: 20px;
  border-bottom: 1px solid #f0f0f0;
}

.map-transport-section:last-child {
  border-bottom: none;
}

.map-transport-title {
  font-size: 18px;
  font-weight: 600;
  font-family: "Roboto", sans-serif;
  color: #333;
  margin-bottom: 10px;
  letter-spacing: 0.5px;
}

.map-transport-details {
  color: #666;
  line-height: 1.6;
  margin: 0;
}

.map-transport-details p {
  margin: 5px 0;
}

.map-transport-note {
  color: #666;
  line-height: 1.6;
  margin-top: 15px;
  font-style: italic;
}

.map-link {
  color: #9acd32;
  cursor: pointer;
  text-decoration: underline;
}

.map-link:hover {
  color: #7aa428;
}

.map-google-link {
  background: none;
  border: 1px solid #ccc;
  color: #333;
  padding: 10px 20px;
  border-radius: 25px;
  font-size: 14px;
  cursor: pointer;
  margin-top: 15px;
  transition: all 0.3s ease;
}

.map-google-link:hover {
  background-color: #f0f0f0;
  border-color: #999;
}

/* Museum Entrances */
.map-entrances-header {
  margin-bottom: 30px;
}

.map-entrances-question {
  font-size: 24px;
  font-weight: 600;
  color: #333;
  margin: 20px 0 15px;
}

.map-entrances-description {
  color: #666;
  line-height: 1.6;
}

/* Entrance Selector */
.map-entrance-selector {
  margin-bottom: 20px;
}

.map-dropdown-container {
  position: relative;
  display: inline-block;
  width: 100%;
  max-width: 300px;
}

.map-dropdown-button {
  width: 100%;
  background-color: #2c2f11;
  border: 1px solid #ddd;
  margin-bottom: 10px;
  padding: 12px 16px;
  border-radius: 12px;
  font-size: 16px;
  font-weight: 500;
  font-family: "Roboto", sans-serif;
  color: #fff;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: all 0.3s ease;
}

.map-dropdown-button:hover {
  border-color: #9acd32;
}

.map-dropdown-menu {
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background-color: #fff;
  border: 1px solid #ddd;
  border-top: none;
  border-radius: 14px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 10;
}

.map-dropdown-item {
  width: 100%;
  background: none;
  border: none;
  padding: 12px 16px;
  text-align: left;
  cursor: pointer;
  font-size: 18px;
  color: #333;
  transition: background-color 0.2s ease;
}

.map-dropdown-item:hover {
  background-color: #f8f8f8;
}

.map-dropdown-item.active {
  background-color: #e8f5e8;
  color: black;
  font-weight: 600;
}

/* View Toggle */
.map-view-toggle {
  display: flex;
  margin-bottom: 30px;
  border-bottom: 1px solid #e0e0e0;
}

.map-toggle-btn {
  background: none;
  border: none;
  padding: 15px 20px;
  font-size: 16px;
  color: #666;
  cursor: pointer;
  border-bottom: 3px solid transparent;
  transition: all 0.3s ease;
}

.map-toggle-btn:hover {
  color: #333;
}

.map-toggle-btn.active {
  color: #333;
  border-bottom-color: #333;
  font-weight: 500;
}

/* Entrances List */
.map-entrances-list {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.map-entrance-item {
  background-color: #fff;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  padding: 20px;
  transition: box-shadow 0.3s ease;
}

.map-entrance-item:hover {
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.map-entrance-header {
  display: flex;
  align-items: center;
  margin-bottom: 15px;
}

.map-entrance-icon {
  width: 40px;
  height: 40px;
  background-color: #f0f0f0;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 15px;
  color: #666;
}

.map-entrance-info {
  flex: 1;
}

.map-entrance-name {
  font-size: 18px;
  font-weight: 600;
  color: #333;
  margin: 0 0 5px;
}

.map-entrance-status {
  font-size: 14px;
  color: #4caf50;
  font-weight: 500;
}

.map-entrance-address {
  color: #666;
  margin-bottom: 10px;
  font-style: italic;
}

.map-entrance-description {
  color: #333;
  line-height: 1.6;
  margin-bottom: 15px;
}

.map-entrance-details {
  padding-left: 20px;
  margin-bottom: 15px;
}

.map-entrance-details li {
  color: #666;
  line-height: 1.6;
  margin-bottom: 8px;
}

.map-entrance-additional p {
  color: #666;
  line-height: 1.6;
  margin: 5px 0;
}

/* Map Placeholder */
.map-placeholder {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #f8f8f8;
  border: 2px dashed #ddd;
  border-radius: 8px;
  padding: 60px 20px;
  margin: 20px 0;
  color: #666;
}

.map-placeholder.large {
  padding: 100px 20px;
}

.map-placeholder svg {
  width: 48px;
  height: 48px;
  margin-bottom: 15px;
  color: #999;
}

.map-placeholder p {
  margin: 0 0 20px;
  font-size: 16px;
}

.map-download-btn {
  background-color: #9acd32;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 25px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.map-download-btn:hover {
  background-color: #8bc34a;
  transform: translateY(-2px);
}

/* Responsive Design */
@media (max-width: 768px) {
  .map-hero-title {
    font-size: 2rem;
  }

  .map-hero-content {
    left: 15px;
    bottom: 30px;
  }

  .map-nav-tabs {
    flex-wrap: wrap;
    justify-content: center;
  }

  .map-tab {
    min-width: 100px;
    padding: 10px 12px;
    font-size: 14px;
    min-height: 44px;
  }

  .map-content-section {
    padding: 15px;
  }

  .map-entrances-question {
    font-size: 20px;
  }

  .map-entrance-header {
    flex-direction: column;
    align-items: flex-start;
  }

  .map-entrance-icon {
    margin-bottom: 10px;
  }
}

@media (max-width: 480px) {
  .map-hero {
    height: 40vh;
  }

  .map-hero-title {
    font-size: 1.7rem;
  }

  .map-back-button {
    top: 15px;
    left: 15px;
    font-size: 14px;
    padding: 6px 12px;
  }

  .map-dropdown-container {
    max-width: 100%;
  }

  .map-view-toggle {
    justify-content: center;
  }

  .map-tab {
    min-width: 90px;
    padding: 8px 10px;
    font-size: 13px;
    min-height: 40px;
  }
}
