/* LifeAtMuseumPage.css */

@font-face {
  font-family: "LouvreSerif";
  src: url("../../assets/fonts/louvreserif/400/latin.woff2") format("woff2");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: "Mythical-Prince";
  src: url("../../assets/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

:root {
  --primary-color: #b38741;
  --primary-dark: #93702f;
  --primary-light: #d3af78;
  --secondary-color: #1e1e1e;
  --text-color: #333333;
  --light-color: #f5f5f5;
  --accent-color: #d4af37;
  --bg-light: #f9f8f6;
  --bg-medium: #f3f0e9;
  --bg-dark: #e9e4d8;
  --shadow-soft: 0 6px 15px rgba(0, 0, 0, 0.08);
  --shadow-medium: 0 10px 25px rgba(0, 0, 0, 0.12);
  --shadow-hard: 0 15px 35px rgba(0, 0, 0, 0.18);
  --transition-slow: 0.6s cubic-bezier(0.19, 1, 0.22, 1);
  --transition-medium: 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
  --transition-fast: 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  --font-primary: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  --font-secondary: "Roboto", sans-serif;
}

.life-museum-page {
  width: 100%;
  overflow-x: hidden;
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  color: var(--text-color);
}

/* Hero Section */
.lm-hero {
  position: relative;
  width: 100%;
  min-height: 60vh;
  height: 60vh;
  max-height: 600px;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.lm-hero-slides-container {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}

.lm-hero-slide {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center 30%;
  opacity: 0;
  transition: opacity 2.5s ease-in-out, transform 15s ease-out;
  transform: scale(1.2);
  filter: saturate(1.2) contrast(1.1) brightness(0.95);
}

.lm-hero-slide.active {
  opacity: 1;
  transform: scale(1);
}

.lm-hero-slide::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(
    circle at 70% 20%,
    rgba(255, 255, 255, 0.1) 0%,
    rgba(0, 0, 0, 0) 60%
  );
  pointer-events: none;
}

.lm-hero-img-wrapper {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  pointer-events: none;
}

.lm-hero-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  display: block;
}

.lm-hero-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(44, 47, 17, 0.3),
    rgba(44, 47, 17, 0.95)
  );
  z-index: 1;
}

.lm-hero-content,
.lm-hero-scroll-indicator {
  position: relative;
  z-index: 2;
}

.lm-hero-title {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 4.5rem;
  font-weight: 600;
  line-height: 1.1;
  margin: 0 0 1.5rem;
  color: #fff;
  max-width: 80%;
  letter-spacing: 0.5px;
  transform: translateY(20px);
  opacity: 0;
  animation: lmFadeInUp 1s ease 0.4s forwards;
}

.lm-hero-title::after {
  content: "";
  position: absolute;
  bottom: -10px;
  left: 0px;
  width: 100px;
  height: 3px;
  background-color: rgba(255, 255, 255, 0.7);
  transform-origin: left center;
  transform: scaleX(0);
  animation: 1.2s ease 0.5s 1 normal forwards running lmExpandWidth;
}

.lm-hero-breadcrumb {
  display: flex;
  align-items: center;
  font-family: Roboto, Arial, sans-serif;
  font-size: 16px;
  font-weight: 300;
  margin: 20px 0px 0px;
  animation: 1.2s ease 0.3s 1 normal both running lmFadeInUp;
  text-shadow: 0 2px 8px rgba(0, 0, 0, 0.5);
}

.lm-breadcrumb-item {
  color: rgba(255, 255, 255, 0.8);
  text-decoration: none;
  transition: color 0.3s ease;
}

.lm-breadcrumb-item:hover {
  color: #fff;
}

.lm-breadcrumb-separator {
  margin: 0 0.5rem;
  color: rgba(255, 255, 255, 0.6);
}

.lm-breadcrumb-active {
  color: #fff;
}

.lm-home-icon {
  display: flex;
  align-items: center;
}

@keyframes lmFadeInUp {
  from {
    opacity: 0;
    transform: translateY(60px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes lmExpandWidth {
  from {
    transform: scaleX(0);
  }
  to {
    transform: scaleX(1);
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* News Section */
.lm-news-section {
  padding: 5rem 0 8rem;
  position: relative;
  overflow: visible;
  max-width: 1400px;
  margin: 0 auto;
}

.lm-section-header {
  text-align: left;
  margin-bottom: 4rem;
  max-width: 1200px;
  margin-left: 5%;
}

.lm-section-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
  position: relative;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: #000;
}

/* Filter options */
.lm-filter-container {
  display: flex;
  align-items: center;
  margin-bottom: 3rem;
  margin-left: 5%;
  flex-wrap: wrap;
}

.lm-filter-label {
  font-family: "Roboto", sans-serif;
  font-size: 16px;
  color: #555;
  margin-right: 1.5rem;
  margin-bottom: 10px;
}

.lm-filter-options {
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
  max-width: 100%;
}

.lm-filter-option {
  font-family: "Roboto", sans-serif;
  font-size: 14px;
  padding: 8px 16px;
  border-radius: 20px;
  border: none;
  background-color: #f0f0f0;
  color: #555;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
  margin-bottom: 8px;
}

.lm-filter-option.active {
  background-color: #2c2f11;
  color: white;
  font-weight: 500;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.lm-filter-option:hover:not(.active) {
  background-color: #e0e0e0;
  transform: translateY(-2px);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

/* News grid */
.lm-news-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 30px;
  margin: 0 5%;
}

.lm-news-item {
  border-radius: 8px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.lm-news-item:hover {
  transform: translateY(-5px);
  box-shadow: var(--shadow-medium);
}

.lm-news-link {
  text-decoration: none;
  color: inherit;
  display: block;
}

.lm-news-image-container {
  position: relative;
  height: 240px;
  overflow: hidden;
}

.lm-news-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.lm-news-item:hover .lm-news-image {
  transform: scale(1.05);
}

.lm-news-categories {
  position: absolute;
  left: 15px;
  bottom: 15px;
  display: flex;
  gap: 8px;
}

.lm-news-category,
.lm-news-subcategory {
  display: inline-block;
  padding: 6px 12px;
  font-size: 12px;
  font-weight: 500;
  font-family: "Roboto", sans-serif;
  border-radius: 3px;
  background-color: #2c2f11;
  color: white;
}

.lm-news-subcategory {
  background-color: #93702f;
}

.lm-news-content {
  padding: 25px;
}

.lm-news-title {
  font-family: "Meholrax", serif;
  font-size: 1.4rem;
  margin-bottom: 15px;
  line-height: 1.3;
  font-weight: 500;
}

.lm-news-excerpt {
  font-family: "Roboto", sans-serif;
  font-size: 15px;
  line-height: 1.6;
  color: #555;
  margin-bottom: 20px;
}

.lm-news-date {
  display: block;
  font-family: "Roboto", sans-serif;
  font-size: 14px;
  color: #777;
  font-weight: 500;
}

/* Donation banner */
.lm-donation-banner {
  background-color: #93702f;
  padding: 12px 20px;
  margin-top: 3rem;
  display: inline-block;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.lm-donation-banner:hover {
  background-color: #7a5c24;
}

.lm-donation-text {
  color: white;
  font-family: "Roboto", sans-serif;
  font-size: 16px;
  font-weight: 500;
}

/* See more button */
.lm-see-more-button {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 80%;
  max-width: 400px;
  margin: 3rem auto 0;
  padding: 16px 16px;
  background-color: #2c2f11;
  color: white;
  font-family: "Roboto", sans-serif;
  font-size: 16px;
  font-weight: 500;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.lm-see-more-button:hover {
  background-color: #2c2f11;
  transform: translateY(-2px);
  box-shadow: var(--shadow-soft);
}

.lm-see-more-button:active {
  transform: translateY(0px);
  box-shadow: none;
}

.lm-see-more-button::after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  width: 5px;
  height: 5px;
  background: rgba(255, 255, 255, 0.3);
  opacity: 0;
  border-radius: 100%;
  transform: scale(1, 1) translate(-50%);
  transform-origin: 50% 50%;
}

.lm-see-more-button:hover::after {
  animation: ripple 1s ease-out;
}

@keyframes ripple {
  0% {
    transform: scale(0, 0);
    opacity: 0.5;
  }
  100% {
    transform: scale(20, 20);
    opacity: 0;
  }
}

.lm-see-more-button .lm-plus-icon {
  margin-right: 8px;
  font-size: 18px;
  transition: transform 0.3s ease;
}

.lm-see-more-button:hover .lm-plus-icon {
  transform: rotate(90deg);
}

/* Hidden news items transition */
.lm-hidden-news {
  opacity: 0;
  max-height: 0;
  overflow: hidden;
  transition: opacity 0.5s ease, max-height 0.8s ease;
}

.lm-hidden-news.visible {
  opacity: 1;
  max-height: 2000px;
}

/* Responsive styles */
@media (max-width: 1200px) {
  .lm-news-grid {
    gap: 20px;
  }
}

@media (max-width: 992px) {
  .lm-hero-title {
    font-size: 3.5rem;
  }

  .lm-section-title {
    font-size: 2.2rem;
  }

  .lm-news-grid {
    grid-template-columns: 1fr;
    max-width: 700px;
    margin: 0 auto;
  }
}

@media (max-width: 768px) {
  .lm-hero {
    min-height: 40vh;
    height: 40vh;
    max-height: 350px;
  }

  .lm-hero-title {
    font-size: 3rem;
    max-width: 100%;
  }

  .lm-hero-content {
    width: 90%;
    left: 5%;
  }

  .lm-section-header,
  .lm-filter-container {
    margin-left: 20px;
  }

  .lm-news-grid {
    margin: 0 20px;
  }

  .lm-news-image-container {
    height: 200px;
  }

  .lm-filter-options {
    flex-wrap: nowrap;
    overflow-x: auto;
    padding-bottom: 10px;
    scrollbar-width: none;
  }

  .lm-filter-options::-webkit-scrollbar {
    display: none;
  }
}

@media (max-width: 576px) {
  .lm-hero-title {
    font-size: 2.5rem;
  }

  .lm-hero-breadcrumb {
    font-size: 14px;
  }

  .lm-news-content {
    padding: 20px;
  }

  .lm-news-title {
    font-size: 1.2rem;
  }

  .lm-filter-container {
    flex-direction: column;
    align-items: flex-start;
  }

  .lm-filter-label {
    margin-bottom: 10px;
  }
}

@media (max-width: 480px) {
  .lm-hero {
    min-height: 32vh;
    height: 32vh;
    max-height: 220px;
  }
}

/* Animation classes */
.animate-section {
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.8s ease, transform 0.8s ease;
}

.animate-section.visible {
  opacity: 1;
  transform: translateY(0);
}
