transforms.result:
.bestseller {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/background/Background2.webp");
  background-repeat: repeat;
  background-position: top center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
}

.bestseller-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0;
}

.bestseller-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 40px;
  padding: 0 20px;
}

.bestseller-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  color: #000;
  margin: 0;
}

.bestseller-view-all {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  color: #000;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 5px;
  transition: opacity 0.3s ease;
}

.bestseller-view-all:hover {
  opacity: 0.7;
}

.bestseller-view-all svg {
  font-size: 1.5rem;
}

.bestseller-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 0;
  width: 100%;
}

.bestseller-card {
  position: relative;
  overflow: hidden;
}

.card-image-container {
  width: 100%;
  aspect-ratio: 1;
  overflow: hidden;
  position: relative;
}

.card-image-container::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to top,
    rgba(0, 0, 0, 0.8),
    rgba(0, 0, 0, 0.4) 50%,
    rgba(0, 0, 0, 0.1)
  );
  pointer-events: none;
}

.card-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.bestseller-card:hover .card-image {
  transform: scale(1.05);
}

.card-content {
  position: absolute;
  top: 70%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  padding: 20px;
  color: #fff;
  text-align: center;
  z-index: 1;
}

.card-title {
  font-family: "Roboto", sans-serif;
  font-size: 1.2rem;
  font-weight: 400;
  margin: 0 0 8px 0;
  line-height: 1.4;
  max-width: 80%;
  margin: 0 auto 8px;
}

.card-price {
  font-family: "Roboto", sans-serif;
  font-size: 1.2rem;
  margin: 0;
  opacity: 0.9;
}

/* Responsive styles */
@media (max-width: 992px) {
  .bestseller-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 768px) {
  .bestseller-title {
    font-size: 2rem;
  }

  .card-title {
    font-size: 1.1rem;
  }
}

@media (max-width: 480px) {
  .bestseller-grid {
    grid-template-columns: 1fr;
  }

  .bestseller-title {
    font-size: 1.8rem;
  }

  .bestseller-view-all {
    font-size: 0.9rem;
  }

  .card-title {
    font-size: 1.4rem;
  }
}
