.block-contents {
  background-color: #2c2f11;
  color: #fff;
  width: 100%;
  display: flex;
  align-items: center;
  padding: 30px 15px; /* Default padding for mobile */
}

.content-container {
  margin: 0 auto;
  width: 100%;
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.content-wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 30px;
}

.content-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  font-weight: normal;
  margin: 0;
  max-width: 400px;
  line-height: 1.2;
}

.artists-list {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 10px;
}

.artist-item {
  font-family: "Roboto", sans-serif;
  font-size: 2.8rem;
  font-weight: 300;
  line-height: 1.2;
  color: #fff;
  transition: opacity 0.3s ease;
}

.artist-item:hover {
  opacity: 0.8;
}

.content-cta {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  color: #fff;
  text-decoration: none;
  margin-top: 20px;
  opacity: 0.8;
  transition: opacity 0.3s ease;
}

.content-cta:hover {
  opacity: 1;
}

.collaboration {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 5px;
  margin-top: 40px;
}

.collaboration-text {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  opacity: 0.7;
}

.collaboration-site {
  font-family: "Roboto", sans-serif;
  font-size: 0.9rem;
  opacity: 0.5;
}

/* Responsive styles */
@media (max-width: 768px) {
  .block-contents {
    padding: 40px 20px;
  }

  .content-title {
    font-size: 2rem;
  }

  .artist-item {
    font-size: 2.2rem;
  }
}

@media (max-width: 480px) {
  .block-contents {
    padding: 30px 15px;
  }

  .content-title {
    font-size: 1.8rem;
  }

  .artist-item {
    font-size: 1.8rem;
  }

  .content-cta {
    font-size: 0.9rem;
  }
}

/* Desktop styles */
@media (min-width: 769px) {
  .block-contents {
    padding: 100px 80px;
    min-height: 700px;
    background: linear-gradient(135deg, #2c2f11 0%, #1a1c0a 100%);
    position: relative;
    overflow: hidden;
  }

  .block-contents::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(
      circle at 30% 20%,
      rgba(255, 255, 255, 0.05) 0%,
      transparent 60%
    );
    pointer-events: none;
  }

  .content-container {
    max-width: 1400px;
    z-index: 1;
  }

  .content-wrapper {
    gap: 60px;
    position: relative;
  }

  .content-title {
    font-size: 4rem;
    max-width: 700px;
    background: linear-gradient(to right, #ffffff, #e0e0e0);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    margin-bottom: 20px;
    position: relative;
  }

  .content-title::after {
    content: "";
    position: absolute;
    left: 0;
    bottom: -15px;
    width: 100px;
    height: 4px;
    background: linear-gradient(90deg, #ffffff 0%, transparent 100%);
  }

  .artists-list {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 40px 80px;
    width: 100%;
  }

  .artist-item {
    font-size: 3.5rem;
    position: relative;
    display: inline-block;
    transform-origin: left;
    transition: transform 0.4s cubic-bezier(0.4, 0, 0.2, 1), color 0.3s ease;
  }

  .artist-item::before {
    content: "";
    position: absolute;
    left: -20px;
    top: 50%;
    width: 0;
    height: 2px;
    background-color: #fff;
    transition: width 0.3s ease;
    transform: translateY(-50%);
  }

  .artist-item:hover {
    transform: scale(1.05);
    color: #e0e0e0;
  }

  .artist-item:hover::before {
    width: 15px;
  }

  .artist-item::after {
    content: "";
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 1px;
    background: linear-gradient(90deg, #ffffff 0%, transparent 100%);
    transform: scaleX(0);
    transition: transform 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    transform-origin: left;
  }

  .artist-item:hover::after {
    transform: scaleX(1);
  }

  .content-cta {
    font-size: 1.3rem;
    margin-top: 60px;
    position: relative;
    padding: 15px 30px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 30px;
    backdrop-filter: blur(5px);
    transition: all 0.4s ease;
  }

  .content-cta:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.4);
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }

  .collaboration {
    margin-top: 80px;
    padding: 30px;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 15px;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .collaboration-text {
    font-size: 1.3rem;
    letter-spacing: 0.5px;
  }

  .collaboration-site {
    font-size: 1.1rem;
    background: linear-gradient(90deg, #ffffff 0%, #a0a0a0 100%);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }
}
