@font-face {
  font-family: "Mythical-Prince";
  src: url("../../../assets/museedupin/1FTV-VIP-Mythical-Prince.otf")
    format("opentype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

.dupin-plus-page {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/v1748784842/background/Background2.webp");
  background-repeat: repeat;
  background-position: center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  box-sizing: border-box;
  overflow: visible;
  color: #2c2f11;
  font-weight: 600;
  min-height: 100vh;
  font-family: "Roboto", sans-serif;
}

.dupin-plus-header {
  padding: 8rem 2rem 4rem;
  position: relative;
  clip-path: polygon(0 0, 100% 0, 100% 92%, 0 100%);
}

.header-content {
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 2;
  text-align: center;
}

.page-title {
  font-family: "Mythical-Prince", "LouvreSerif", Georgia, "Times New Roman",
    serif;
  font-size: 4rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
  color: #b38741;
  letter-spacing: 2px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  animation: fadeInUp 1s ease;
}

.page-subtitle {
  font-size: 1.4rem;
  max-width: 700px;
  margin: 0 auto;
  font-weight: 300;
  line-height: 1.6;
  opacity: 0.9;
  font-family: "Roboto", serif;
  animation: fadeInUp 1s ease 0.3s both;
}

/* Filter Section */
.filter-section {
  background-color: #2c2f11;
  padding: 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 1.5rem;
  max-width: 1400px;
  margin: -3rem auto 0;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
  position: relative;
  z-index: 10;
}

.search-box {
  display: flex;
  align-items: center;
  background-color: rgba(255, 255, 255, 0.1);
  border-radius: 30px;
  padding: 0.8rem 1.5rem;
  width: 100%;
  max-width: 400px;
  transition: all 0.3s ease;
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: #fff !important;
}

.search-box:focus-within {
  background-color: rgba(255, 255, 255, 0.15);
  border-color: #b38741;
  box-shadow: 0 0 0 2px rgba(179, 135, 65, 0.2);
}

.search-input {
  background: transparent;
  border: none;
  color: #fff !important;
  font-size: 1.1rem;
  width: 100%;
  padding: 0.5rem 0;
  outline: none;
  font-family: "Roboto", sans-serif;
}

.search-input::placeholder {
  color: rgba(255, 255, 255, 0.6);
}

.search-button {
  background: none !important;
  border: none;
  color: #b38741;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
  margin-left: 0.5rem;
  opacity: 0.7;
  transition: opacity 0.3s ease;
}

.search-button:hover {
  opacity: 1;
}

.category-filters {
  display: flex;
  align-items: center;
  gap: 0.8rem;
  flex-wrap: wrap;
}

.category-button {
  background-color: transparent;
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 30px;
  cursor: pointer;
  font-size: 1rem;
  transition: all 0.3s ease;
  font-family: "Roboto", serif;
  letter-spacing: 0.5px;
}

.category-button:hover {
  background-color: rgba(179, 135, 65, 0.1);
  border-color: #b38741;
}

.category-button.active {
  background-color: #b38741;
  border-color: #b38741;
  color: #2c2f11;
}

/* Videos Grid */
.videos-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  gap: 2.5rem;
  padding: 4rem 2rem;
  max-width: 1400px;
  margin: 0 auto;
}

.video-card {
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 10px 30px -5px rgba(0, 0, 0, 0.3);
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  cursor: pointer;
  height: 100%;
  color: #000;
  display: flex;
  flex-direction: column;
  border: 1px solid rgba(179, 135, 65, 0.2);
}

.video-card:hover {
  transform: translateY(-10px);
  box-shadow: 0 15px 40px -5px rgba(0, 0, 0, 0.4);
  border-color: #b38741;
}

.video-thumbnail-container {
  position: relative;
  width: 100%;
  padding-top: 56.25%;
  overflow: hidden;
}

.video-thumbnail {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.video-card:hover .video-thumbnail {
  transform: scale(1.05);
}

.play-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(44, 47, 17, 0.2),
    rgba(44, 47, 17, 0.5)
  );
  opacity: 0;
  transition: opacity 0.4s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.video-card:hover .play-overlay {
  opacity: 1;
}

.play-button {
  width: 70px;
  height: 70px;
  transform: scale(0.9);
  transition: transform 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.video-card:hover .play-button {
  transform: scale(1);
}

.play-icon {
  width: 100%;
  height: 100%;
}

.play-circle {
  fill: rgba(44, 47, 17, 0.8);
  stroke: #b38741;
  stroke-width: 2;
}

.play-triangle {
  fill: #b38741;
}

.video-duration {
  position: absolute;
  bottom: 15px;
  right: 15px;
  color: #000;
  padding: 0.4rem 0.8rem;
  border-radius: 4px;
  font-size: 0.9rem;
  font-weight: 500;
  letter-spacing: 0.5px;
}

.video-info {
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  flex-grow: 1;
}

.video-category {
  display: inline-block;
  background-color: rgba(179, 135, 65, 0.1);
  padding: 0.4rem 1rem;
  border-radius: 4px;
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #b38741;
  align-self: flex-start;
}

.video-title {
  font-size: 1.4rem;
  font-weight: 500;
  margin: 0;
  line-height: 1.4;
  font-family: "Meholrax", serif;
  color: #000;
  letter-spacing: 0.5px;
}

.video-subtitle {
  font-size: 1rem;
  opacity: 0.7;
  font-family: "Roboto", serif;
  color: #000;
}

.video-description {
  font-size: 1rem;
  line-height: 1.6;
  opacity: 0.8;
  margin: 0;
  overflow: hidden;
  display: -webkit-box;
  font-family: "Roboto", serif;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  color: #000;
}

/* No Results */
.no-results {
  grid-column: 1 / -1;
  text-align: center;
  padding: 4rem 1rem;
  background-color: rgba(44, 47, 17, 0.95);
  border-radius: 12px;
  border: 1px solid rgba(179, 135, 65, 0.2);
}

.no-results h3 {
  font-size: 1.8rem;
  margin-bottom: 1rem;
  color: #b38741;
  font-family: "Meholrax", serif;
}

.no-results p {
  opacity: 0.7;
  color: #fff;
  font-size: 1.1rem;
}

/* Video Modal */
.video-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(44, 47, 17, 0.95);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  animation: modalFadeIn 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
}

@keyframes modalFadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.video-modal-content {
  width: 90%;
  max-width: 1000px;
  position: relative;
  background-color: #2c2f11;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.7);
  animation: modalContentSlideIn 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  border: 1px solid rgba(179, 135, 65, 0.3);
}

@keyframes modalContentSlideIn {
  from {
    transform: translateY(50px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.close-modal {
  position: absolute;
  top: 20px;
  right: 20px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: rgba(44, 47, 17, 0.9);
  border: 1px solid rgba(179, 135, 65, 0.3);
  color: #b38741;
  font-size: 24px;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10;
  transition: all 0.3s ease;
}

.close-modal:hover {
  background-color: rgba(179, 135, 65, 0.2);
  transform: rotate(90deg);
}

.video-container {
  position: relative;
  padding-top: 56.25%;
  width: 100%;
  background-color: #000;
}

.video-container iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: none;
}

.video-modal-info {
  padding: 2rem;
  border-top: 1px solid rgba(179, 135, 65, 0.2);
}

.video-modal-header {
  display: flex;
  flex-wrap: wrap;
  align-items: flex-start;
  gap: 1rem;
  margin-bottom: 1.5rem;
}

.video-modal-title {
  width: 100%;
  font-size: 2rem;
  margin: 0;
  font-weight: 500;
  color: #b38741;
  font-family: "Meholrax", serif;
  letter-spacing: 0.5px;
}

.video-modal-subtitle {
  font-size: 1.2rem;
  opacity: 0.8;
  margin: 0;
  width: 100%;
  color: rgba(255, 255, 255, 0.8);
}

.video-modal-category {
  display: inline-block;
  background-color: rgba(179, 135, 65, 0.1);
  padding: 0.4rem 1rem;
  border-radius: 4px;
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #b38741;
}

.video-modal-duration {
  font-size: 1rem;
  opacity: 0.6;
  color: #b38741;
}

.video-modal-description {
  font-size: 1.1rem;
  line-height: 1.6;
  opacity: 0.8;
  margin: 0;
  color: rgba(255, 255, 255, 0.9);
}

/* Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Media Queries */
@media (max-width: 900px) {
  .page-title {
    font-size: 3rem;
  }

  .page-subtitle {
    font-size: 1.2rem;
  }

  .filter-section {
    flex-direction: column;
    align-items: stretch;
    margin-top: -2rem;
  }

  .search-box {
    max-width: 100%;
  }

  .videos-grid {
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 1.5rem;
    padding: 3rem 1.5rem;
  }

  .video-modal-title {
    font-size: 1.5rem;
  }

  .video-modal-description {
    font-size: 1rem;
  }
}

@media (max-width: 600px) {
  .dupin-plus-header {
    padding: 6rem 1.5rem 3rem;
  }

  .page-title {
    font-size: 2.5rem;
  }

  .page-subtitle {
    font-size: 1rem;
  }

  .filter-section {
    padding: 1.5rem;
    margin-top: -1.5rem;
  }

  .videos-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }

  .category-button {
    padding: 0.6rem 1rem;
    font-size: 0.9rem;
  }

  .video-modal-content {
    width: 95%;
  }

  .video-modal-info {
    padding: 1.5rem;
  }

  .video-modal-title {
    font-size: 1.3rem;
  }
}
