.blocks-wrapper {
  background-image: url("https://res.cloudinary.com/dn0br7hj0/image/upload/background/Background2.webp");
  background-repeat: repeat;
  background-position: top center;
  background-size: 300px;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

.blocks-container {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
}

@media (min-width: 769px) {
  .blocks-container {
    flex-direction: row;
    padding: 2rem;
    gap: 2rem;
  }

  .block {
    flex: 1;
    height: calc(100vh - 4rem);
    border-radius: 20px;
    transition: all 0.5s ease;
  }

  .block:hover {
    flex: 1.2;
  }

  .block-image-container {
    border-radius: 20px;
  }

  .block-content {
    padding-top: 0;
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.3s ease;
  }

  .block:hover .block-content {
    opacity: 1;
    transform: translateY(0);
  }

  .block-title {
    font-size: 3.5rem;
    margin-bottom: 1rem;
  }

  .block-description {
    font-size: 1.1rem;
    margin-bottom: 1.5rem;
    max-width: 80%;
    line-height: 1.6;
    opacity: 0;
    transform: translateY(10px);
    transition: all 0.3s ease 0.1s;
  }

  .block:hover .block-description {
    opacity: 1;
    transform: translateY(0);
  }

  .block-subtitle {
    font-size: 1.3rem;
    opacity: 0;
    transform: translateY(10px);
    transition: all 0.3s ease 0.2s;
  }

  .block:hover .block-subtitle {
    opacity: 1;
    transform: translateY(0);
  }

  .block::after {
    background: linear-gradient(
      to bottom,
      rgba(0, 0, 0, 0.1),
      rgba(0, 0, 0, 0.7)
    );
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .block:hover::after {
    opacity: 1;
  }
}

.block {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.block-image-container {
  width: 100%;
  height: 100%;
  overflow: hidden;
  position: absolute;
  top: 0;
  left: 0;
}

.block-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.block:hover .block-image {
  transform: scale(1.05);
}

.block-content {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  color: #fff;
  z-index: 2;
  padding-top: 150px;
  max-width: 80%;
}

.block-title {
  font-family: "Meholrax", serif;
  font-size: 2.5rem;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
}

.block-description {
  font-family: "Roboto", sans-serif;
  font-size: 1rem;
  margin-bottom: 1rem;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
  display: none;
}

.block-subtitle {
  font-family: "Roboto", sans-serif;
  font-size: 1.1rem;
  display: flex;
  align-items: center;
  gap: 5px;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
}

.arrow-icon {
  font-size: 1.5rem;
  transition: transform 0.3s ease;
}

.block:hover .arrow-icon {
  transform: translateX(5px);
}

.block::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.3),
    rgba(0, 0, 0, 0.5)
  );
  pointer-events: none;
}

/* Responsive styles */
@media (max-width: 768px) {
  .block-content {
    max-width: 90%;
  }

  .block-title {
    font-size: 2rem;
  }

  .block-subtitle {
    font-size: 1rem;
  }

  .block-description {
    display: none;
  }
}

@media (max-width: 480px) {
  .block-content {
    max-width: 95%;
  }

  .block-title {
    font-size: 1.8rem;
  }

  .block-subtitle {
    font-size: 0.9rem;
  }
}
